INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 1, 'CHAPTER I', 'The Distress of Arjuna');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 1, 'Dhritirashtra:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 2, 'Ranged thus for battle on the sacred plain--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 3, 'On Kurukshetra--say, Sanjaya! say');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 4, 'What wrought my people, and the Pandavas?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 5, 'Sanjaya:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 6, 'When he beheld the host of Pandavas,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 7, 'Raja Duryodhana to Drona drew,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 8, 'And spake these words: \"Ah, Guru! see this line,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 9, 'How vast it is of Pandu fighting-men,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 10, 'Embattled by the son of Drupada,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 11, 'Thy scholar in the war! Therein stand ranked');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 12, 'Chiefs like Arjuna, like to Bhima chiefs,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 13, 'Benders of bows; Virata, Yuyudhan,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 14, 'Drupada, eminent upon his car,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 15, 'Dhrishtaket, Chekitan, Kasi\'s stout lord,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 16, 'Purujit, Kuntibhoj, and Saivya,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 17, 'With Yudhamanyu, and Uttamauj');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 18, 'Subhadra\'s child; and Drupadi\'s;-all famed!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 19, 'All mounted on their shining chariots!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 20, 'On our side, too,--thou best of Brahmans! see');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 21, 'Excellent chiefs, commanders of my line,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 22, 'Whose names I joy to count: thyself the first,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 23, 'Then Bhishma, Karna, Kripa fierce in fight,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 24, 'Vikarna, Aswatthaman; next to these');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 25, 'Strong Saumadatti, with full many more');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 26, 'Valiant and tried, ready this day to die');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 27, 'For me their king, each with his weapon grasped,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 28, 'Each skilful in the field. Weakest-meseems-');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 29, 'Our battle shows where Bhishma holds command,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 30, 'And Bhima, fronting him, something too strong!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 31, 'Have care our captains nigh to Bhishma\'s ranks');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 32, 'Prepare what help they may! Now, blow my shell!\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 33, 'Then, at the signal of the aged king,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 34, 'With blare to wake the blood, rolling around');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 35, 'Like to a lion\'s roar, the trumpeter');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 36, 'Blew the great Conch; and, at the noise of it,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 37, 'Trumpets and drums, cymbals and gongs and horns');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 38, 'Burst into sudden clamour; as the blasts');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 39, 'Of loosened tempest, such the tumult seemed!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 40, 'Then might be seen, upon their car of gold');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 41, 'Yoked with white steeds, blowing their battle-shells,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 42, 'Krishna the God, Arjuna at his side:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 43, 'Krishna, with knotted locks, blew his great conch');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 44, 'Carved of the \"Giant\'s bone;\" Arjuna blew');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 45, 'Indra\'s loud gift; Bhima the terrible--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 46, 'Wolf-bellied Bhima-blew a long reed-conch;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 47, 'And Yudhisthira, Kunti\'s blameless son,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 48, 'Winded a mighty shell, \"Victory\'s Voice;\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 49, 'And Nakula blew shrill upon his conch');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 50, 'Named the \"Sweet-sounding,\" Sahadev on his');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 51, 'Called\"Gem-bedecked,\" and Kasi\'s Prince on his.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 52, 'Sikhandi on his car, Dhrishtadyumn,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 53, 'Virata, Satyaki the Unsubdued,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 54, 'Drupada, with his sons, (O Lord of Earth!)');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 55, 'Long-armed Subhadra\'s children, all blew loud,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 56, 'So that the clangour shook their foemen\'s hearts,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 57, 'With quaking earth and thundering heav\'n.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 58, 'Then \'twas-');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 59, 'Beholding Dhritirashtra\'s battle set,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 60, 'Weapons unsheathing, bows drawn forth, the war');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 61, 'Instant to break-Arjun, whose ensign-badge');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 62, 'Was Hanuman the monkey, spake this thing');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 63, 'To Krishna the Divine, his charioteer:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 64, '\"Drive, Dauntless One! to yonder open ground');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 65, 'Betwixt the armies; I would see more nigh');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 66, 'These who will fight with us, those we must slay');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 67, 'To-day, in war\'s arbitrament; for, sure,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 68, 'On bloodshed all are bent who throng this plain,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 69, 'Obeying Dhritirashtra\'s sinful son.\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 70, 'Thus, by Arjuna prayed, (O Bharata!)');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 71, 'Between the hosts that heavenly Charioteer');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 72, 'Drove the bright car, reining its milk-white steeds');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 73, 'Where Bhishma led,and Drona,and their Lords.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 74, '\"See!\" spake he to Arjuna, \"where they stand,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 75, 'Thy kindred of the Kurus:\" and the Prince');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 76, 'Marked on each hand the kinsmen of his house,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 77, 'Grandsires and sires, uncles and brothers and sons,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 78, 'Cousins and sons-in-law and nephews, mixed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 79, 'With friends and honoured elders; some this side,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 80, 'Some that side ranged: and, seeing those opposed,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 81, 'Such kith grown enemies-Arjuna\'s heart');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 82, 'Melted with pity, while he uttered this:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 83, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 84, 'Krishna! as I behold, come here to shed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 85, 'Their common blood, yon concourse of our kin,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 86, 'My members fail, my tongue dries in my mouth,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 87, 'A shudder thrills my body, and my hair');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 88, 'Bristles with horror; from my weak hand slips');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 89, 'Gandiv, the goodly bow; a fever burns');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 90, 'My skin to parching; hardly may I stand;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 91, 'The life within me seems to swim and faint;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 92, 'Nothing do I foresee save woe and wail!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 93, 'It is not good, O Keshav! nought of good');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 94, 'Can spring from mutual slaughter! Lo, I hate');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 95, 'Triumph and domination, wealth and ease,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 96, 'Thus sadly won! Aho! what victory');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 97, 'Can bring delight, Govinda! what rich spoils');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 98, 'Could profit; what rule recompense; what span');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 99, 'Of life itself seem sweet, bought with such blood?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 100, 'Seeing that these stand here, ready to die,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 101, 'For whose sake life was fair, and pleasure pleased,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 102, 'And power grew precious:-grandsires, sires, and sons,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 103, 'Brothers, and fathers-in-law, and sons-in-law,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 104, 'Elders and friends! Shall I deal death on these');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 105, 'Even though they seek to slay us? Not one blow,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 106, 'O Madhusudan! will I strike to gain');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 107, 'The rule of all Three Worlds; then, how much less');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 108, 'To seize an earthly kingdom! Killing these');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 109, 'Must breed but anguish, Krishna! If they be');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 110, 'Guilty, we shall grow guilty by their deaths;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 111, 'Their sins will light on us, if we shall slay');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 112, 'Those sons of Dhritirashtra, and our kin;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 113, 'What peace could come of that, O Madhava?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 114, 'For if indeed, blinded by lust and wrath,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 115, 'These cannot see, or will not see, the sin');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 116, 'Of kingly lines o\'erthrown and kinsmen slain,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 117, 'How should not we, who see, shun such a crime--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 118, 'We who perceive the guilt and feel the shame--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 119, 'O thou Delight of Men, Janardana?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 120, 'By overthrow of houses perisheth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 121, 'Their sweet continuous household piety,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 122, 'And-rites neglected, piety extinct--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 123, 'Enters impiety upon that home;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 124, 'Its women grow unwomaned, whence there spring');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 125, 'Mad passions, and the mingling-up of castes,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 126, 'Sending a Hell-ward road that family,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 127, 'And whoso wrought its doom by wicked wrath.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 128, 'Nay, and the souls of honoured ancestors');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 129, 'Fall from their place of peace, being bereft');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 130, 'Of funeral-cakes and the wan death-water.[FN#1]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 131, 'So teach our holy hymns. Thus, if we slay');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 132, 'Kinsfolk and friends for love of earthly power,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 133, 'Ahovat! what an evil fault it were!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 134, 'Better I deem it, if my kinsmen strike,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 135, 'To face them weaponless, and bare my breast');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 136, 'To shaft and spear, than answer blow with blow.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 137, 'So speaking, in the face of those two hosts,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 138, 'Arjuna sank upon his chariot-seat,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 139, 'And let fall bow and arrows, sick at heart.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 140, 'HERE ENDETH CHAPTER I. OF THE BHAGAVAD-GITA,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 141, 'Entitled \"Arjun-Vishad,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 1, 142, 'Or \"The Book of the Distress of Arjuna.\"');
INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 2, 'CHAPTER II', 'The Book of Doctrines');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 1, 'Sanjaya.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 2, 'Him, filled with such compassion and such grief,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 3, 'With eyes tear-dimmed, despondent, in stern words');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 4, 'The Driver, Madhusudan, thus addressed:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 5, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 6, 'How hath this weakness taken thee? Whence springs');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 7, 'The inglorious trouble, shameful to the brave,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 8, 'Barring the path of virtue? Nay, Arjun!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 9, 'Forbid thyself to feebleness! it mars');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 10, 'Thy warrior-name! cast off the coward-fit!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 11, 'Wake! Be thyself! Arise, Scourge of thy Foes!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 12, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 13, 'How can I, in the battle, shoot with shafts');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 14, 'On Bhishma, or on Drona-O thou Chief!--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 15, 'Both worshipful, both honourable men?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 16, 'Better to live on beggar\'s bread');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 17, 'With those we love alive,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 18, 'Than taste their blood in rich feasts spread,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 19, 'And guiltily survive!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 20, 'Ah! were it worse-who knows?--to be');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 21, 'Victor or vanquished here,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 22, 'When those confront us angrily');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 23, 'Whose death leaves living drear?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 24, 'In pity lost, by doubtings tossed,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 25, 'My thoughts-distracted-turn');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 26, 'To Thee, the Guide I reverence most,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 27, 'That I may counsel learn:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 28, 'I know not what would heal the grief');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 29, 'Burned into soul and sense,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 30, 'If I were earth\'s unchallenged chief--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 31, 'A god--and these gone thence!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 32, 'Sanjaya.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 33, 'So spake Arjuna to the Lord of Hearts,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 34, 'And sighing,\"I will not fight!\" held silence then.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 35, 'To whom, with tender smile, (O Bharata! )');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 36, 'While the Prince wept despairing \'twixt those hosts,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 37, 'Krishna made answer in divinest verse:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 38, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 39, 'Thou grievest where no grief should be! thou speak\'st');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 40, 'Words lacking wisdom! for the wise in heart');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 41, 'Mourn not for those that live, nor those that die.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 42, 'Nor I, nor thou, nor any one of these,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 43, 'Ever was not, nor ever will not be,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 44, 'For ever and for ever afterwards.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 45, 'All, that doth live, lives always! To man\'s frame');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 46, 'As there come infancy and youth and age,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 47, 'So come there raisings-up and layings-down');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 48, 'Of other and of other life-abodes,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 49, 'Which the wise know, and fear not. This that irks--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 50, 'Thy sense-life, thrilling to the elements--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 51, 'Bringing thee heat and cold, sorrows and joys,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 52, '\'Tis brief and mutable! Bear with it, Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 53, 'As the wise bear. The soul which is not moved,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 54, 'The soul that with a strong and constant calm');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 55, 'Takes sorrow and takes joy indifferently,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 56, 'Lives in the life undying! That which is');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 57, 'Can never cease to be; that which is not');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 58, 'Will not exist. To see this truth of both');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 59, 'Is theirs who part essence from accident,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 60, 'Substance from shadow. Indestructible,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 61, 'Learn thou! the Life is, spreading life through all;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 62, 'It cannot anywhere, by any means,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 63, 'Be anywise diminished, stayed, or changed.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 64, 'But for these fleeting frames which it informs');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 65, 'With spirit deathless, endless, infinite,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 66, 'They perish. Let them perish, Prince! and fight!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 67, 'He who shall say, \"Lo! I have slain a man!\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 68, 'He who shall think, \"Lo! I am slain!\" those both');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 69, 'Know naught! Life cannot slay. Life is not slain!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 70, 'Never the spirit was born; the spirit shall cease to be never;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 71, 'Never was time it was not; End and Beginning are dreams!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 72, 'Birthless and deathless and changeless remaineth the spirit for ever;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 73, 'Death hath not touched it at all, dead though the house of it seems!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 74, 'Who knoweth it exhaustless, self-sustained,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 75, 'Immortal, indestructible,--shall such');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 76, 'Say, \"I have killed a man, or caused to kill?\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 77, 'Nay, but as when one layeth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 78, 'His worn-out robes away,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 79, 'And taking new ones, sayeth,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 80, '\"These will I wear to-day!\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 81, 'So putteth by the spirit');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 82, 'Lightly its garb of flesh,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 83, 'And passeth to inherit');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 84, 'A residence afresh.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 85, 'I say to thee weapons reach not the Life;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 86, 'Flame burns it not, waters cannot o\'erwhelm,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 87, 'Nor dry winds wither it. Impenetrable,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 88, 'Unentered, unassailed, unharmed, untouched,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 89, 'Immortal, all-arriving, stable, sure,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 90, 'Invisible, ineffable, by word');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 91, 'And thought uncompassed, ever all itself,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 92, 'Thus is the Soul declared! How wilt thou, then,--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 93, 'Knowing it so,--grieve when thou shouldst not grieve?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 94, 'How, if thou hearest that the man new-dead');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 95, 'Is, like the man new-born, still living man--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 96, 'One same, existent Spirit--wilt thou weep?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 97, 'The end of birth is death; the end of death');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 98, 'Is birth: this is ordained! and mournest thou,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 99, 'Chief of the stalwart arm! for what befalls');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 100, 'Which could not otherwise befall? The birth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 101, 'Of living things comes unperceived; the death');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 102, 'Comes unperceived; between them, beings perceive:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 103, 'What is there sorrowful herein, dear Prince?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 104, 'Wonderful, wistful, to contemplate!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 105, 'Difficult, doubtful, to speak upon!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 106, 'Strange and great for tongue to relate,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 107, 'Mystical hearing for every one!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 108, 'Nor wotteth man this, what a marvel it is,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 109, 'When seeing, and saying, and hearing are done!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 110, 'This Life within all living things, my Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 111, 'Hides beyond harm; scorn thou to suffer, then,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 112, 'For that which cannot suffer. Do thy part!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 113, 'Be mindful of thy name, and tremble not!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 114, 'Nought better can betide a martial soul');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 115, 'Than lawful war; happy the warrior');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 116, 'To whom comes joy of battle--comes, as now,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 117, 'Glorious and fair, unsought; opening for him');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 118, 'A gateway unto Heav\'n. But, if thou shunn\'st');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 119, 'This honourable field--a Kshattriya--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 120, 'If, knowing thy duty and thy task, thou bidd\'st');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 121, 'Duty and task go by--that shall be sin!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 122, 'And those to come shall speak thee infamy');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 123, 'From age to age; but infamy is worse');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 124, 'For men of noble blood to bear than death!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 125, 'The chiefs upon their battle-chariots');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 126, 'Will deem \'twas fear that drove thee from the fray.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 127, 'Of those who held thee mighty-souled the scorn');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 128, 'Thou must abide, while all thine enemies');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 129, 'Will scatter bitter speech of thee, to mock');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 130, 'The valour which thou hadst; what fate could fall');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 131, 'More grievously than this? Either--being killed--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 132, 'Thou wilt win Swarga\'s safety, or--alive');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 133, 'And victor--thou wilt reign an earthly king.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 134, 'Therefore, arise, thou Son of Kunti! brace');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 135, 'Thine arm for conflict, nerve thy heart to meet--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 136, 'As things alike to thee--pleasure or pain,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 137, 'Profit or ruin, victory or defeat:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 138, 'So minded, gird thee to the fight, for so');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 139, 'Thou shalt not sin!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 140, 'Thus far I speak to thee');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 141, 'As from the \"Sankhya\"--unspiritually--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 142, 'Hear now the deeper teaching of the Yog,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 143, 'Which holding, understanding, thou shalt burst');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 144, 'Thy Karmabandh, the bondage of wrought deeds.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 145, 'Here shall no end be hindered, no hope marred,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 146, 'No loss be feared: faith--yea, a little faith--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 147, 'Shall save thee from the anguish of thy dread.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 148, 'Here, Glory of the Kurus! shines one rule--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 149, 'One steadfast rule--while shifting souls have laws');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 150, 'Many and hard. Specious, but wrongful deem');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 151, 'The speech of those ill-taught ones who extol');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 152, 'The letter of their Vedas, saying, \"This');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 153, 'Is all we have, or need;\" being weak at heart');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 154, 'With wants, seekers of Heaven: which comes--they say--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 155, 'As \"fruit of good deeds done;\" promising men');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 156, 'Much profit in new births for works of faith;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 157, 'In various rites abounding; following whereon');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 158, 'Large merit shall accrue towards wealth and power;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 159, 'Albeit, who wealth and power do most desire');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 160, 'Least fixity of soul have such, least hold');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 161, 'On heavenly meditation. Much these teach,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 162, 'From Veds, concerning the \"three qualities;\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 163, 'But thou, be free of the \"three qualities,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 164, 'Free of the \"pairs of opposites,\"[FN#2] and free');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 165, 'From that sad righteousness which calculates;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 166, 'Self-ruled, Arjuna! simple, satisfied![FN#3]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 167, 'Look! like as when a tank pours water forth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 168, 'To suit all needs, so do these Brahmans draw');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 169, 'Text for all wants from tank of Holy Writ.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 170, 'But thou, want not! ask not! Find full reward');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 171, 'Of doing right in right! Let right deeds be');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 172, 'Thy motive, not the fruit which comes from them.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 173, 'And live in action! Labour! Make thine acts');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 174, 'Thy piety, casting all self aside,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 175, 'Contemning gain and merit; equable');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 176, 'In good or evil: equability');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 177, 'Is Yog, is piety!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 178, 'Yet, the right act');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 179, 'Is less, far less, than the right-thinking mind.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 180, 'Seek refuge in thy soul; have there thy heaven!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 181, 'Scorn them that follow virtue for her gifts!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 182, 'The mind of pure devotion--even here--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 183, 'Casts equally aside good deeds and bad,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 184, 'Passing above them. Unto pure devotion');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 185, 'Devote thyself: with perfect meditation');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 186, 'Comes perfect act, and the right-hearted rise--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 187, 'More certainly because they seek no gain--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 188, 'Forth from the bands of body, step by step,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 189, 'To highest seats of bliss. When thy firm soul');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 190, 'Hath shaken off those tangled oracles');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 191, 'Which ignorantly guide, then shall it soar');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 192, 'To high neglect of what\'s denied or said,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 193, 'This way or that way, in doctrinal writ.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 194, 'Troubled no longer by the priestly lore,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 195, 'Safe shall it live, and sure; steadfastly bent');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 196, 'On meditation. This is Yog--and Peace!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 197, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 198, 'What is his mark who hath that steadfast heart,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 199, 'Confirmed in holy meditation? How');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 200, 'Know we his speech, Kesava? Sits he, moves he');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 201, 'Like other men?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 202, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 203, 'When one, O Pritha\'s Son!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 204, 'Abandoning desires which shake the mind--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 205, 'Finds in his soul full comfort for his soul,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 206, 'He hath attained the Yog--that man is such!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 207, 'In sorrows not dejected, and in joys');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 208, 'Not overjoyed; dwelling outside the stress');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 209, 'Of passion, fear, and anger; fixed in calms');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 210, 'Of lofty contemplation;--such an one');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 211, 'Is Muni, is the Sage, the true Recluse!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 212, 'He who to none and nowhere overbound');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 213, 'By ties of flesh, takes evil things and good');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 214, 'Neither desponding nor exulting, such');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 215, 'Bears wisdom\'s plainest mark! He who shall draw');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 216, 'As the wise tortoise draws its four feet safe');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 217, 'Under its shield, his five frail senses back');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 218, 'Under the spirit\'s buckler from the world');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 219, 'Which else assails them, such an one, my Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 220, 'Hath wisdom\'s mark! Things that solicit sense');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 221, 'Hold off from the self-governed; nay, it comes,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 222, 'The appetites of him who lives beyond');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 223, 'Depart,--aroused no more. Yet may it chance,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 224, 'O Son of Kunti! that a governed mind');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 225, 'Shall some time feel the sense-storms sweep, and wrest');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 226, 'Strong self-control by the roots. Let him regain');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 227, 'His kingdom! let him conquer this, and sit');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 228, 'On Me intent. That man alone is wise');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 229, 'Who keeps the mastery of himself! If one');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 230, 'Ponders on objects of the sense, there springs');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 231, 'Attraction; from attraction grows desire,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 232, 'Desire flames to fierce passion, passion breeds');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 233, 'Recklessness; then the memory--all betrayed--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 234, 'Lets noble purpose go, and saps the mind,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 235, 'Till purpose, mind, and man are all undone.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 236, 'But, if one deals with objects of the sense');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 237, 'Not loving and not hating, making them');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 238, 'Serve his free soul, which rests serenely lord,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 239, 'Lo! such a man comes to tranquillity;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 240, 'And out of that tranquillity shall rise');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 241, 'The end and healing of his earthly pains,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 242, 'Since the will governed sets the soul at peace.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 243, 'The soul of the ungoverned is not his,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 244, 'Nor hath he knowledge of himself; which lacked,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 245, 'How grows serenity? and, wanting that,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 246, 'Whence shall he hope for happiness?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 247, 'The mind');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 248, 'That gives itself to follow shows of sense');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 249, 'Seeth its helm of wisdom rent away,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 250, 'And, like a ship in waves of whirlwind, drives');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 251, 'To wreck and death. Only with him, great Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 252, 'Whose senses are not swayed by things of sense--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 253, 'Only with him who holds his mastery,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 254, 'Shows wisdom perfect. What is midnight-gloom');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 255, 'To unenlightened souls shines wakeful day');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 256, 'To his clear gaze; what seems as wakeful day');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 257, 'Is known for night, thick night of ignorance,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 258, 'To his true-seeing eyes. Such is the Saint!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 259, 'And like the ocean, day by day receiving');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 260, 'Floods from all lands, which never overflows');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 261, 'Its boundary-line not leaping, and not leaving,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 262, 'Fed by the rivers, but unswelled by those;--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 263, 'So is the perfect one! to his soul\'s ocean');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 264, 'The world of sense pours streams of witchery;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 265, 'They leave him as they find, without commotion,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 266, 'Taking their tribute, but remaining sea.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 267, 'Yea! whoso, shaking off the yoke of flesh');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 268, 'Lives lord, not servant, of his lusts; set free');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 269, 'From pride, from passion, from the sin of \"Self,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 270, 'Toucheth tranquillity! O Pritha\'s Son!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 271, 'That is the state of Brahm! There rests no dread');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 272, 'When that last step is reached! Live where he will,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 273, 'Die when he may, such passeth from all \'plaining,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 274, 'To blest Nirvana, with the Gods, attaining.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 275, 'HERE ENDETH CHAPTER II. OF THE BHAGAVAD-GITA,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 276, 'Entitled \"Sankhya-Yog,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 2, 277, 'Or \"The Book of Doctrines.\"');
INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 3, 'CHAPTER III', 'Virtue in Work');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 1, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 2, 'Thou whom all mortals praise, Janardana!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 3, 'If meditation be a nobler thing');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 4, 'Than action, wherefore, then, great Kesava!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 5, 'Dost thou impel me to this dreadful fight?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 6, 'Now am I by thy doubtful speech disturbed!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 7, 'Tell me one thing, and tell me certainly;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 8, 'By what road shall I find the better end?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 9, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 10, 'I told thee, blameless Lord! there be two paths');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 11, 'Shown to this world; two schools of wisdom.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 12, 'First');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 13, 'The Sankhya\'s, which doth save in way of works');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 14, 'Prescribed[FN#4] by reason; next, the Yog, which bids');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 15, 'Attain by meditation, spiritually:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 16, 'Yet these are one! No man shall \'scape from act');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 17, 'By shunning action; nay, and none shall come');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 18, 'By mere renouncements unto perfectness.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 19, 'Nay, and no jot of time, at any time,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 20, 'Rests any actionless; his nature\'s law');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 21, 'Compels him, even unwilling, into act;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 22, '[For thought is act in fancy]. He who sits');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 23, 'Suppressing all the instruments of flesh,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 24, 'Yet in his idle heart thinking on them,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 25, 'Plays the inept and guilty hypocrite:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 26, 'But he who, with strong body serving mind,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 27, 'Gives up his mortal powers to worthy work,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 28, 'Not seeking gain, Arjuna! such an one');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 29, 'Is honourable. Do thine allotted task!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 30, 'Work is more excellent than idleness;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 31, 'The body\'s life proceeds not, lacking work.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 32, 'There is a task of holiness to do,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 33, 'Unlike world-binding toil, which bindeth not');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 34, 'The faithful soul; such earthly duty do');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 35, 'Free from desire, and thou shalt well perform');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 36, 'Thy heavenly purpose. Spake Prajapati--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 37, 'In the beginning, when all men were made,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 38, 'And, with mankind, the sacrifice-- \"Do this!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 39, 'Work! sacrifice! Increase and multiply');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 40, 'With sacrifice! This shall be Kamaduk,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 41, 'Your \'Cow of Plenty,\' giving back her milk');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 42, 'Of all abundance. Worship the gods thereby;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 43, 'The gods shall yield thee grace. Those meats ye crave');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 44, 'The gods will grant to Labour, when it pays');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 45, 'Tithes in the altar-flame. But if one eats');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 46, 'Fruits of the earth, rendering to kindly Heaven');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 47, 'No gift of toil, that thief steals from his world.\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 48, 'Who eat of food after their sacrifice');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 49, 'Are quit of fault, but they that spread a feast');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 50, 'All for themselves, eat sin and drink of sin.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 51, 'By food the living live; food comes of rain,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 52, 'And rain comes by the pious sacrifice,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 53, 'And sacrifice is paid with tithes of toil;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 54, 'Thus action is of Brahma, who is One,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 55, 'The Only, All-pervading; at all times');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 56, 'Present in sacrifice. He that abstains');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 57, 'To help the rolling wheels of this great world,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 58, 'Glutting his idle sense, lives a lost life,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 59, 'Shameful and vain. Existing for himself,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 60, 'Self-concentrated, serving self alone,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 61, 'No part hath he in aught; nothing achieved,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 62, 'Nought wrought or unwrought toucheth him; no hope');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 63, 'Of help for all the living things of earth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 64, 'Depends from him.[FN#5] Therefore, thy task prescribed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 65, 'With spirit unattached gladly perform,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 66, 'Since in performance of plain duty man');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 67, 'Mounts to his highest bliss. By works alone');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 68, 'Janak and ancient saints reached blessedness!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 69, 'Moreover, for the upholding of thy kind,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 70, 'Action thou should\'st embrace. What the wise choose');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 71, 'The unwise people take; what best men do');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 72, 'The multitude will follow. Look on me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 73, 'Thou Son of Pritha! in the three wide worlds');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 74, 'I am not bound to any toil, no height');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 75, 'Awaits to scale, no gift remains to gain,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 76, 'Yet I act here! and, if I acted not--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 77, 'Earnest and watchful--those that look to me');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 78, 'For guidance, sinking back to sloth again');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 79, 'Because I slumbered, would decline from good,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 80, 'And I should break earth\'s order and commit');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 81, 'Her offspring unto ruin, Bharata!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 82, 'Even as the unknowing toil, wedded to sense,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 83, 'So let the enlightened toil, sense-freed, but set');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 84, 'To bring the world deliverance, and its bliss;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 85, 'Not sowing in those simple, busy hearts');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 86, 'Seed of despair. Yea! let each play his part');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 87, 'In all he finds to do, with unyoked soul.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 88, 'All things are everywhere by Nature wrought');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 89, 'In interaction of the qualities.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 90, 'The fool, cheated by self, thinks, \"This I did\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 91, 'And \"That I wrought; \"but--ah, thou strong-armed Prince!--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 92, 'A better-lessoned mind, knowing the play');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 93, 'Of visible things within the world of sense,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 94, 'And how the qualities must qualify,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 95, 'Standeth aloof even from his acts. Th\' untaught');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 96, 'Live mixed with them, knowing not Nature\'s way,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 97, 'Of highest aims unwitting, slow and dull.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 98, 'Those make thou not to stumble, having the light;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 99, 'But all thy dues discharging, for My sake,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 100, 'With meditation centred inwardly,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 101, 'Seeking no profit, satisfied, serene,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 102, 'Heedless of issue--fight! They who shall keep');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 103, 'My ordinance thus, the wise and willing hearts,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 104, 'Have quittance from all issue of their acts;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 105, 'But those who disregard My ordinance,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 106, 'Thinking they know, know nought, and fall to loss,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 107, 'Confused and foolish. \'Sooth, the instructed one');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 108, 'Doth of his kind, following what fits him most:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 109, 'And lower creatures of their kind; in vain');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 110, 'Contending \'gainst the law. Needs must it be');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 111, 'The objects of the sense will stir the sense');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 112, 'To like and dislike, yet th\' enlightened man');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 113, 'Yields not to these, knowing them enemies.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 114, 'Finally, this is better, that one do');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 115, 'His own task as he may, even though he fail,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 116, 'Than take tasks not his own, though they seem good.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 117, 'To die performing duty is no ill;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 118, 'But who seeks other roads shall wander still.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 119, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 120, 'Yet tell me, Teacher! by what force doth man');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 121, 'Go to his ill, unwilling; as if one');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 122, 'Pushed him that evil path?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 123, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 124, 'Kama it is!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 125, 'Passion it is! born of the Darknesses,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 126, 'Which pusheth him. Mighty of appetite,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 127, 'Sinful, and strong is this!--man\'s enemy!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 128, 'As smoke blots the white fire, as clinging rust');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 129, 'Mars the bright mirror, as the womb surrounds');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 130, 'The babe unborn, so is the world of things');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 131, 'Foiled, soiled, enclosed in this desire of flesh.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 132, 'The wise fall, caught in it; the unresting foe');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 133, 'It is of wisdom, wearing countless forms,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 134, 'Fair but deceitful, subtle as a flame.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 135, 'Sense, mind, and reason--these, O Kunti\'s Son!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 136, 'Are booty for it; in its play with these');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 137, 'It maddens man, beguiling, blinding him.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 138, 'Therefore, thou noblest child of Bharata!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 139, 'Govern thy heart! Constrain th\' entangled sense!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 140, 'Resist the false, soft sinfulness which saps');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 141, 'Knowledge and judgment! Yea, the world is strong,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 142, 'But what discerns it stronger, and the mind');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 143, 'Strongest; and high o\'er all the ruling Soul.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 144, 'Wherefore, perceiving Him who reigns supreme,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 145, 'Put forth full force of Soul in thy own soul!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 146, 'Fight! vanquish foes and doubts, dear Hero! slay');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 147, 'What haunts thee in fond shapes, and would betray!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 148, 'HERE ENDETH CHAPTER III. OF THE BHAGAVAD-GITA,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 149, 'Entitled \"Karma-Yog,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 3, 150, 'Or \"The Book of Virtue in Work.\"');
INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 4, 'CHAPTER IV', 'The Religion of Knowledge');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 1, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 2, 'This deathless Yoga, this deep union,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 3, 'I taught Vivaswata,[FN#6] the Lord of Light;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 4, 'Vivaswata to Manu gave it; he');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 5, 'To Ikshwaku; so passed it down the line');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 6, 'Of all my royal Rishis. Then, with years,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 7, 'The truth grew dim and perished, noble Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 8, 'Now once again to thee it is declared--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 9, 'This ancient lore, this mystery supreme--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 10, 'Seeing I find thee votary and friend.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 11, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 12, 'Thy birth, dear Lord, was in these later days,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 13, 'And bright Vivaswata\'s preceded time!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 14, 'How shall I comprehend this thing thou sayest,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 15, '\"From the beginning it was I who taught?\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 16, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 17, 'Manifold the renewals of my birth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 18, 'Have been, Arjuna! and of thy births, too!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 19, 'But mine I know, and thine thou knowest not,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 20, 'O Slayer of thy Foes! Albeit I be');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 21, 'Unborn, undying, indestructible,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 22, 'The Lord of all things living; not the less--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 23, 'By Maya, by my magic which I stamp');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 24, 'On floating Nature-forms, the primal vast--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 25, 'I come, and go, and come. When Righteousness');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 26, 'Declines, O Bharata! when Wickedness');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 27, 'Is strong, I rise, from age to age, and take');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 28, 'Visible shape, and move a man with men,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 29, 'Succouring the good, thrusting the evil back,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 30, 'And setting Virtue on her seat again.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 31, 'Who knows the truth touching my births on earth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 32, 'And my divine work, when he quits the flesh');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 33, 'Puts on its load no more, falls no more down');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 34, 'To earthly birth: to Me he comes, dear Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 35, 'Many there be who come! from fear set free,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 36, 'From anger, from desire; keeping their hearts');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 37, 'Fixed upon me--my Faithful--purified');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 38, 'By sacred flame of Knowledge. Such as these');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 39, 'Mix with my being. Whoso worship me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 40, 'Them I exalt; but all men everywhere');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 41, 'Shall fall into my path; albeit, those souls');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 42, 'Which seek reward for works, make sacrifice');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 43, 'Now, to the lower gods. I say to thee');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 44, 'Here have they their reward. But I am He');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 45, 'Made the Four Castes, and portioned them a place');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 46, 'After their qualities and gifts. Yea, I');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 47, 'Created, the Reposeful; I that live');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 48, 'Immortally, made all those mortal births:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 49, 'For works soil not my essence, being works');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 50, 'Wrought uninvolved.[FN#7] Who knows me acting thus');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 51, 'Unchained by action, action binds not him;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 52, 'And, so perceiving, all those saints of old');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 53, 'Worked, seeking for deliverance. Work thou');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 54, 'As, in the days gone by, thy fathers did.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 55, 'Thou sayst, perplexed, It hath been asked before');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 56, 'By singers and by sages, \"What is act,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 57, 'And what inaction? \"I will teach thee this,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 58, 'And, knowing, thou shalt learn which work doth save');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 59, 'Needs must one rightly meditate those three--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 60, 'Doing,--not doing,--and undoing. Here');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 61, 'Thorny and dark the path is! He who sees');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 62, 'How action may be rest, rest action--he');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 63, 'Is wisest \'mid his kind; he hath the truth!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 64, 'He doeth well, acting or resting. Freed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 65, 'In all his works from prickings of desire,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 66, 'Burned clean in act by the white fire of truth,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 67, 'The wise call that man wise; and such an one,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 68, 'Renouncing fruit of deeds, always content.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 69, 'Always self-satisfying, if he works,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 70, 'Doth nothing that shall stain his separate soul,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 71, 'Which--quit of fear and hope--subduing self--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 72, 'Rejecting outward impulse--yielding up');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 73, 'To body\'s need nothing save body, dwells');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 74, 'Sinless amid all sin, with equal calm');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 75, 'Taking what may befall, by grief unmoved,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 76, 'Unmoved by joy, unenvyingly; the same');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 77, 'In good and evil fortunes; nowise bound');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 78, 'By bond of deeds. Nay, but of such an one,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 79, 'Whose crave is gone, whose soul is liberate,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 80, 'Whose heart is set on truth--of such an one');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 81, 'What work he does is work of sacrifice,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 82, 'Which passeth purely into ash and smoke');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 83, 'Consumed upon the altar! All\'s then God!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 84, 'The sacrifice is Brahm, the ghee and grain');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 85, 'Are Brahm, the fire is Brahm, the flesh it eats');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 86, 'Is Brahm, and unto Brahm attaineth he');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 87, 'Who, in such office, meditates on Brahm.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 88, 'Some votaries there be who serve the gods');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 89, 'With flesh and altar-smoke; but other some');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 90, 'Who, lighting subtler fires, make purer rite');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 91, 'With will of worship. Of the which be they');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 92, 'Who, in white flame of continence, consume');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 93, 'Joys of the sense, delights of eye and ear,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 94, 'Forgoing tender speech and sound of song:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 95, 'And they who, kindling fires with torch of Truth,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 96, 'Burn on a hidden altar-stone the bliss');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 97, 'Of youth and love, renouncing happiness:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 98, 'And they who lay for offering there their wealth,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 99, 'Their penance, meditation, piety,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 100, 'Their steadfast reading of the scrolls, their lore');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 101, 'Painfully gained with long austerities:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 102, 'And they who, making silent sacrifice,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 103, 'Draw in their breath to feed the flame of thought,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 104, 'And breathe it forth to waft the heart on high,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 105, 'Governing the ventage of each entering air');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 106, 'Lest one sigh pass which helpeth not the soul:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 107, 'And they who, day by day denying needs,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 108, 'Lay life itself upon the altar-flame,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 109, 'Burning the body wan. Lo! all these keep');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 110, 'The rite of offering, as if they slew');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 111, 'Victims; and all thereby efface much sin.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 112, 'Yea! and who feed on the immortal food');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 113, 'Left of such sacrifice, to Brahma pass,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 114, 'To The Unending. But for him that makes');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 115, 'No sacrifice, he hath nor part nor lot');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 116, 'Even in the present world. How should he share');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 117, 'Another, O thou Glory of thy Line?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 118, 'In sight of Brahma all these offerings');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 119, 'Are spread and are accepted! Comprehend');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 120, 'That all proceed by act; for knowing this,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 121, 'Thou shalt be quit of doubt. The sacrifice');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 122, 'Which Knowledge pays is better than great gifts');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 123, 'Offered by wealth, since gifts\' worth--O my Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 124, 'Lies in the mind which gives, the will that serves:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 125, 'And these are gained by reverence, by strong search,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 126, 'By humble heed of those who see the Truth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 127, 'And teach it. Knowing Truth, thy heart no more');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 128, 'Will ache with error, for the Truth shall show');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 129, 'All things subdued to thee, as thou to Me.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 130, 'Moreover, Son of Pandu! wert thou worst');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 131, 'Of all wrong-doers, this fair ship of Truth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 132, 'Should bear thee safe and dry across the sea');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 133, 'Of thy transgressions. As the kindled flame');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 134, 'Feeds on the fuel till it sinks to ash,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 135, 'So unto ash, Arjuna! unto nought');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 136, 'The flame of Knowledge wastes works\' dross away!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 137, 'There is no purifier like thereto');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 138, 'In all this world, and he who seeketh it');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 139, 'Shall find it--being grown perfect--in himself.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 140, 'Believing, he receives it when the soul');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 141, 'Masters itself, and cleaves to Truth, and comes--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 142, 'Possessing knowledge--to the higher peace,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 143, 'The uttermost repose. But those untaught,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 144, 'And those without full faith, and those who fear');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 145, 'Are shent; no peace is here or other where,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 146, 'No hope, nor happiness for whoso doubts.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 147, 'He that, being self-contained, hath vanquished doubt,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 148, 'Disparting self from service, soul from works,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 149, 'Enlightened and emancipate, my Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 150, 'Works fetter him no more! Cut then atwain');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 151, 'With sword of wisdom, Son of Bharata!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 152, 'This doubt that binds thy heart-beats! cleave the bond');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 153, 'Born of thy ignorance! Be bold and wise!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 154, 'Give thyself to the field with me! Arise!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 155, 'HERE ENDETH CHAPTER IV. OF THE BHAGAVAD-GITA,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 156, 'Entitled \"Jnana Yog,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 4, 157, 'Or \"The Book of the Religion of Knowledge,\"');
INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 5, 'CHAPTER V', 'Religion of Renouncing Works');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 1, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 2, 'Yet, Krishna! at the one time thou dost laud');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 3, 'Surcease of works, and, at another time,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 4, 'Service through work. Of these twain plainly tell');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 5, 'Which is the better way?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 6, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 7, 'To cease from works');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 8, 'Is well, and to do works in holiness');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 9, 'Is well; and both conduct to bliss supreme;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 10, 'But of these twain the better way is his');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 11, 'Who working piously refraineth not.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 12, 'That is the true Renouncer, firm and fixed,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 13, 'Who--seeking nought, rejecting nought--dwells proof');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 14, 'Against the \"opposites.\"[FN#8] O valiant Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 15, 'In doing, such breaks lightly from all deed:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 16, '\'Tis the new scholar talks as they were two,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 17, 'This Sankhya and this Yoga: wise men know');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 18, 'Who husbands one plucks golden fruit of both!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 19, 'The region of high rest which Sankhyans reach');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 20, 'Yogins attain. Who sees these twain as one');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 21, 'Sees with clear eyes! Yet such abstraction, Chief!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 22, 'Is hard to win without much holiness.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 23, 'Whoso is fixed in holiness, self-ruled,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 24, 'Pure-hearted, lord of senses and of self,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 25, 'Lost in the common life of all which lives--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 26, 'A \"Yogayukt\"--he is a Saint who wends');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 27, 'Straightway to Brahm. Such an one is not touched');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 28, 'By taint of deeds. \"Nought of myself I do!\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 29, 'Thus will he think-who holds the truth of truths--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 30, 'In seeing, hearing, touching, smelling; when');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 31, 'He eats, or goes, or breathes; slumbers or talks,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 32, 'Holds fast or loosens, opes his eyes or shuts;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 33, 'Always assured \"This is the sense-world plays');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 34, 'With senses.\"He that acts in thought of Brahm,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 35, 'Detaching end from act, with act content,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 36, 'The world of sense can no more stain his soul');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 37, 'Than waters mar th\' enamelled lotus-leaf.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 38, 'With life, with heart, with mind,-nay, with the help');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 39, 'Of all five senses--letting selfhood go--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 40, 'Yogins toil ever towards their souls\' release.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 41, 'Such votaries, renouncing fruit of deeds,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 42, 'Gain endless peace: the unvowed, the passion-bound,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 43, 'Seeking a fruit from works, are fastened down.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 44, 'The embodied sage, withdrawn within his soul,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 45, 'At every act sits godlike in \"the town');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 46, 'Which hath nine gateways,\"[FN#9] neither doing aught');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 47, 'Nor causing any deed. This world\'s Lord makes');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 48, 'Neither the work, nor passion for the work,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 49, 'Nor lust for fruit of work; the man\'s own self');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 50, 'Pushes to these! The Master of this World');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 51, 'Takes on himself the good or evil deeds');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 52, 'Of no man--dwelling beyond! Mankind errs here');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 53, 'By folly, darkening knowledge. But, for whom');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 54, 'That darkness of the soul is chased by light,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 55, 'Splendid and clear shines manifest the Truth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 56, 'As if a Sun of Wisdom sprang to shed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 57, 'Its beams of dawn. Him meditating still,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 58, 'Him seeking, with Him blended, stayed on Him,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 59, 'The souls illuminated take that road');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 60, 'Which hath no turning back--their sins flung off');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 61, 'By strength of faith. [Who will may have this Light;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 62, 'Who hath it sees.] To him who wisely sees,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 63, 'The Brahman with his scrolls and sanctities,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 64, 'The cow, the elephant, the unclean dog,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 65, 'The Outcast gorging dog\'s meat, are all one.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 66, 'The world is overcome--aye! even here!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 67, 'By such as fix their faith on Unity.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 68, 'The sinless Brahma dwells in Unity,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 69, 'And they in Brahma. Be not over-glad');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 70, 'Attaining joy, and be not over-sad');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 71, 'Encountering grief, but, stayed on Brahma, still');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 72, 'Constant let each abide! The sage whose soul');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 73, 'Holds off from outer contacts, in himself');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 74, 'Finds bliss; to Brahma joined by piety,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 75, 'His spirit tastes eternal peace. The joys');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 76, 'Springing from sense-life are but quickening wombs');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 77, 'Which breed sure griefs: those joys begin and end!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 78, 'The wise mind takes no pleasure, Kunti\'s Son!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 79, 'In such as those! But if a man shall learn,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 80, 'Even while he lives and bears his body\'s chain,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 81, 'To master lust and anger, he is blest!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 82, 'He is the Yukta; he hath happiness,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 83, 'Contentment, light, within: his life is merged');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 84, 'In Brahma\'s life; he doth Nirvana touch!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 85, 'Thus go the Rishis unto rest, who dwell');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 86, 'With sins effaced, with doubts at end, with hearts');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 87, 'Governed and calm. Glad in all good they live,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 88, 'Nigh to the peace of God; and all those live');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 89, 'Who pass their days exempt from greed and wrath,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 90, 'Subduing self and senses, knowing the Soul!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 91, 'The Saint who shuts outside his placid soul');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 92, 'All touch of sense, letting no contact through;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 93, 'Whose quiet eyes gaze straight from fixed brows,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 94, 'Whose outward breath and inward breath are drawn');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 95, 'Equal and slow through nostrils still and close;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 96, 'That one-with organs, heart, and mind constrained,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 97, 'Bent on deliverance, having put away');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 98, 'Passion, and fear, and rage;--hath, even now,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 99, 'Obtained deliverance, ever and ever freed.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 100, 'Yea! for he knows Me Who am He that heeds');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 101, 'The sacrifice and worship, God revealed;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 102, 'And He who heeds not, being Lord of Worlds,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 103, 'Lover of all that lives, God unrevealed,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 104, 'Wherein who will shall find surety and shield!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 105, 'HERE ENDS CHAPTER V. OF THE BHAGAVAD-GITA,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 106, 'Entitled \"Karmasanyasayog,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 5, 107, 'Or \"The Book of Religion by Renouncing Fruit of Works.\"');
INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 6, 'CHAPTER VI', 'Religion by Self-restraint');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 1, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 2, 'Therefore, who doeth work rightful to do,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 3, 'Not seeking gain from work, that man, O Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 4, 'Is Sanyasi and Yogi--both in one');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 5, 'And he is neither who lights not the flame');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 6, 'Of sacrifice, nor setteth hand to task.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 7, 'Regard as true Renouncer him that makes');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 8, 'Worship by work, for who renounceth not');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 9, 'Works not as Yogin. So is that well said:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 10, '\"By works the votary doth rise to faith,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 11, 'And saintship is the ceasing from all works;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 12, 'Because the perfect Yogin acts--but acts');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 13, 'Unmoved by passions and unbound by deeds,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 14, 'Setting result aside.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 15, 'Let each man raise');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 16, 'The Self by Soul, not trample down his Self,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 17, 'Since Soul that is Self\'s friend may grow Self\'s foe.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 18, 'Soul is Self\'s friend when Self doth rule o\'er Self,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 19, 'But Self turns enemy if Soul\'s own self');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 20, 'Hates Self as not itself.[FN#10]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 21, 'The sovereign soul');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 22, 'Of him who lives self-governed and at peace');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 23, 'Is centred in itself, taking alike');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 24, 'Pleasure and pain; heat, cold; glory and shame.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 25, 'He is the Yogi, he is Yukta, glad');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 26, 'With joy of light and truth; dwelling apart');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 27, 'Upon a peak, with senses subjugate');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 28, 'Whereto the clod, the rock, the glistering gold');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 29, 'Show all as one. By this sign is he known');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 30, 'Being of equal grace to comrades, friends,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 31, 'Chance-comers, strangers, lovers, enemies,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 32, 'Aliens and kinsmen; loving all alike,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 33, 'Evil or good.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 34, 'Sequestered should he sit,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 35, 'Steadfastly meditating, solitary,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 36, 'His thoughts controlled, his passions laid away,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 37, 'Quit of belongings. In a fair, still spot');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 38, 'Having his fixed abode,--not too much raised,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 39, 'Nor yet too low,--let him abide, his goods');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 40, 'A cloth, a deerskin, and the Kusa-grass.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 41, 'There, setting hard his mind upon The One,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 42, 'Restraining heart and senses, silent, calm,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 43, 'Let him accomplish Yoga, and achieve');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 44, 'Pureness of soul, holding immovable');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 45, 'Body and neck and head, his gaze absorbed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 46, 'Upon his nose-end,[FN#11] rapt from all around,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 47, 'Tranquil in spirit, free of fear, intent');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 48, 'Upon his Brahmacharya vow, devout,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 49, 'Musing on Me, lost in the thought of Me.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 50, 'That Yojin, so devoted, so controlled,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 51, 'Comes to the peace beyond,--My peace, the peace');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 52, 'Of high Nirvana!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 53, 'But for earthly needs');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 54, 'Religion is not his who too much fasts');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 55, 'Or too much feasts, nor his who sleeps away');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 56, 'An idle mind; nor his who wears to waste');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 57, 'His strength in vigils. Nay, Arjuna! call');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 58, 'That the true piety which most removes');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 59, 'Earth-aches and ills, where one is moderate');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 60, 'In eating and in resting, and in sport;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 61, 'Measured in wish and act; sleeping betimes,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 62, 'Waking betimes for duty.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 63, 'When the man,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 64, 'So living, centres on his soul the thought');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 65, 'Straitly restrained--untouched internally');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 66, 'By stress of sense--then is he Yukta. See!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 67, 'Steadfast a lamp burns sheltered from the wind;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 68, 'Such is the likeness of the Yogi\'s mind');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 69, 'Shut from sense-storms and burning bright to Heaven.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 70, 'When mind broods placid, soothed with holy wont;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 71, 'When Self contemplates self, and in itself');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 72, 'Hath comfort; when it knows the nameless joy');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 73, 'Beyond all scope of sense, revealed to soul--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 74, 'Only to soul! and, knowing, wavers not,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 75, 'True to the farther Truth; when, holding this,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 76, 'It deems no other treasure comparable,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 77, 'But, harboured there, cannot be stirred or shook');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 78, 'By any gravest grief, call that state \"peace,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 79, 'That happy severance Yoga; call that man');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 80, 'The perfect Yogin!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 81, 'Steadfastly the will');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 82, 'Must toil thereto, till efforts end in ease,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 83, 'And thought has passed from thinking. Shaking off');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 84, 'All longings bred by dreams of fame and gain,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 85, 'Shutting the doorways of the senses close');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 86, 'With watchful ward; so, step by step, it comes');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 87, 'To gift of peace assured and heart assuaged,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 88, 'When the mind dwells self-wrapped, and the soul broods');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 89, 'Cumberless. But, as often as the heart');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 90, 'Breaks--wild and wavering--from control, so oft');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 91, 'Let him re-curb it, let him rein it back');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 92, 'To the soul\'s governance; for perfect bliss');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 93, 'Grows only in the bosom tranquillised,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 94, 'The spirit passionless, purged from offence,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 95, 'Vowed to the Infinite. He who thus vows');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 96, 'His soul to the Supreme Soul, quitting sin,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 97, 'Passes unhindered to the endless bliss');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 98, 'Of unity with Brahma. He so vowed,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 99, 'So blended, sees the Life-Soul resident');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 100, 'In all things living, and all living things');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 101, 'In that Life-Soul contained. And whoso thus');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 102, 'Discerneth Me in all, and all in Me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 103, 'I never let him go; nor looseneth he');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 104, 'Hold upon Me; but, dwell he where he may,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 105, 'Whate\'er his life, in Me he dwells and lives,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 106, 'Because he knows and worships Me, Who dwell');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 107, 'In all which lives, and cleaves to Me in all.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 108, 'Arjuna! if a man sees everywhere--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 109, 'Taught by his own similitude--one Life,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 110, 'One Essence in the Evil and the Good,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 111, 'Hold him a Yogi, yea! well-perfected!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 112, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 113, 'Slayer of Madhu! yet again, this Yog,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 114, 'This Peace, derived from equanimity,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 115, 'Made known by thee--I see no fixity');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 116, 'Therein, no rest, because the heart of men');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 117, 'Is unfixed, Krishna! rash, tumultuous,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 118, 'Wilful and strong. It were all one, I think,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 119, 'To hold the wayward wind, as tame man\'s heart.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 120, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 121, 'Hero long-armed! beyond denial, hard');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 122, 'Man\'s heart is to restrain, and wavering;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 123, 'Yet may it grow restrained by habit, Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 124, 'By wont of self-command. This Yog, I say,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 125, 'Cometh not lightly to th\' ungoverned ones;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 126, 'But he who will be master of himself');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 127, 'Shall win it, if he stoutly strive thereto.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 128, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 129, 'And what road goeth he who, having faith,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 130, 'Fails, Krishna! in the striving; falling back');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 131, 'From holiness, missing the perfect rule?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 132, 'Is he not lost, straying from Brahma\'s light,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 133, 'Like the vain cloud, which floats \'twixt earth and heaven');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 134, 'When lightning splits it, and it vanisheth?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 135, 'Fain would I hear thee answer me herein,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 136, 'Since, Krishna! none save thou can clear the doubt.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 137, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 138, 'He is not lost, thou Son of Pritha! No!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 139, 'Nor earth, nor heaven is forfeit, even for him,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 140, 'Because no heart that holds one right desire');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 141, 'Treadeth the road of loss! He who should fail,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 142, 'Desiring righteousness, cometh at death');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 143, 'Unto the Region of the Just; dwells there');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 144, 'Measureless years, and being born anew,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 145, 'Beginneth life again in some fair home');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 146, 'Amid the mild and happy. It may chance');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 147, 'He doth descend into a Yogin house');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 148, 'On Virtue\'s breast; but that is rare! Such birth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 149, 'Is hard to be obtained on this earth, Chief!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 150, 'So hath he back again what heights of heart');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 151, 'He did achieve, and so he strives anew');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 152, 'To perfectness, with better hope, dear Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 153, 'For by the old desire he is drawn on');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 154, 'Unwittingly; and only to desire');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 155, 'The purity of Yog is to pass');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 156, 'Beyond the Sabdabrahm, the spoken Ved.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 157, 'But, being Yogi, striving strong and long,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 158, 'Purged from transgressions, perfected by births');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 159, 'Following on births, he plants his feet at last');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 160, 'Upon the farther path. Such as one ranks');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 161, 'Above ascetics, higher than the wise,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 162, 'Beyond achievers of vast deeds! Be thou');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 163, 'Yogi Arjuna! And of such believe,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 164, 'Truest and best is he who worships Me');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 165, 'With inmost soul, stayed on My Mystery!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 166, 'HERE ENDETH CHAPTER VI. OF THE BHAGAVAD-GITA,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 167, 'Entitled \"Atmasanyamayog,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 6, 168, 'Or \"The Book of Religion by Self-Restraint.\"');
INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 7, 'CHAPTER VII', 'Religion by Discernment');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 1, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 2, 'Learn now, dear Prince! how, if thy soul be set');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 3, 'Ever on Me--still exercising Yog,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 4, 'Still making Me thy Refuge--thou shalt come');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 5, 'Most surely unto perfect hold of Me.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 6, 'I will declare to thee that utmost lore,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 7, 'Whole and particular, which, when thou knowest,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 8, 'Leaveth no more to know here in this world.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 9, 'Of many thousand mortals, one, perchance,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 10, 'Striveth for Truth; and of those few that strive--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 11, 'Nay, and rise high--one only--here and there--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 12, 'Knoweth Me, as I am, the very Truth.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 13, 'Earth, water, flame, air, ether, life, and mind,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 14, 'And individuality--those eight');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 15, 'Make up the showing of Me, Manifest.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 16, 'These be my lower Nature; learn the higher,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 17, 'Whereby, thou Valiant One! this Universe');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 18, 'Is, by its principle of life, produced;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 19, 'Whereby the worlds of visible things are born');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 20, 'As from a Yoni. Know! I am that womb:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 21, 'I make and I unmake this Universe:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 22, 'Than me there is no other Master, Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 23, 'No other Maker! All these hang on me');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 24, 'As hangs a row of pearls upon its string.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 25, 'I am the fresh taste of the water; I');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 26, 'The silver of the moon, the gold o\' the sun,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 27, 'The word of worship in the Veds, the thrill');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 28, 'That passeth in the ether, and the strength');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 29, 'Of man\'s shed seed. I am the good sweet smell');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 30, 'Of the moistened earth, I am the fire\'s red light,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 31, 'The vital air moving in all which moves,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 32, 'The holiness of hallowed souls, the root');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 33, 'Undying, whence hath sprung whatever is;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 34, 'The wisdom of the wise, the intellect');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 35, 'Of the informed, the greatness of the great.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 36, 'The splendour of the splendid. Kunti\'s Son!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 37, 'These am I, free from passion and desire;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 38, 'Yet am I right desire in all who yearn,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 39, 'Chief of the Bharatas! for all those moods,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 40, 'Soothfast, or passionate, or ignorant,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 41, 'Which Nature frames, deduce from me; but all');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 42, 'Are merged in me--not I in them! The world--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 43, 'Deceived by those three qualities of being--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 44, 'Wotteth not Me Who am outside them all,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 45, 'Above them all, Eternal! Hard it is');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 46, 'To pierce that veil divine of various shows');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 47, 'Which hideth Me; yet they who worship Me');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 48, 'Pierce it and pass beyond.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 49, 'I am not known');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 50, 'To evil-doers, nor to foolish ones,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 51, 'Nor to the base and churlish; nor to those');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 52, 'Whose mind is cheated by the show of things,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 53, 'Nor those that take the way of Asuras.[FN#12]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 54, 'Four sorts of mortals know me: he who weeps,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 55, 'Arjuna! and the man who yearns to know;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 56, 'And he who toils to help; and he who sits');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 57, 'Certain of me, enlightened.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 58, 'Of these four,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 59, 'O Prince of India! highest, nearest, best');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 60, 'That last is, the devout soul, wise, intent');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 61, 'Upon \"The One.\" Dear, above all, am I');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 62, 'To him; and he is dearest unto me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 63, 'All four are good, and seek me; but mine own,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 64, 'The true of heart, the faithful--stayed on me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 65, 'Taking me as their utmost blessedness,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 66, 'They are not \"mine,\"but I--even I myself!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 67, 'At end of many births to Me they come!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 68, 'Yet hard the wise Mahatma is to find,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 69, 'That man who sayeth, \"All is Vasudev!\"[FN#13]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 70, 'There be those, too, whose knowledge, turned aside');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 71, 'By this desire or that, gives them to serve');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 72, 'Some lower gods, with various rites, constrained');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 73, 'By that which mouldeth them. Unto all such--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 74, 'Worship what shrine they will, what shapes, in faith--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 75, '\'Tis I who give them faith! I am content!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 76, 'The heart thus asking favour from its God,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 77, 'Darkened but ardent, hath the end it craves,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 78, 'The lesser blessing--but \'tis I who give!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 79, 'Yet soon is withered what small fruit they reap:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 80, 'Those men of little minds, who worship so,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 81, 'Go where they worship, passing with their gods.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 82, 'But Mine come unto me! Blind are the eyes');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 83, 'Which deem th\' Unmanifested manifest,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 84, 'Not comprehending Me in my true Self!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 85, 'Imperishable, viewless, undeclared,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 86, 'Hidden behind my magic veil of shows,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 87, 'I am not seen by all; I am not known--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 88, 'Unborn and changeless--to the idle world.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 89, 'But I, Arjuna! know all things which were,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 90, 'And all which are, and all which are to be,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 91, 'Albeit not one among them knoweth Me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 92, 'By passion for the \"pairs of opposites,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 93, 'By those twain snares of Like and Dislike, Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 94, 'All creatures live bewildered, save some few');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 95, 'Who, quit of sins, holy in act, informed,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 96, 'Freed from the \"opposites,\"and fixed in faith,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 97, 'Cleave unto Me.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 98, 'Who cleave, who seek in Me');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 99, 'Refuge from birth[FN#14] and death, those have the Truth!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 100, 'Those know Me BRAHMA; know Me Soul of Souls,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 101, 'The ADHYATMAN; know KARMA, my work;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 102, 'Know I am ADHIBHUTA, Lord of Life,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 103, 'And ADHIDAIVA, Lord of all the Gods,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 104, 'And ADHIYAJNA, Lord of Sacrifice;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 105, 'Worship Me well, with hearts of love and faith,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 106, 'And find and hold me in the hour of death.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 107, 'HERE ENDETH CHAPTER VII. OF THE BHAGAVAD-GITA,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 108, 'Entitled \"Vijnanayog,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 7, 109, 'Or \"The Book of Religion by Discernment.\"');
INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 8, 'CHAPTER VIII', 'Religion by Service of the Supreme');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 1, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 2, 'Who is that BRAHMA? What that Soul of Souls,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 3, 'The ADHYATMAN? What, Thou Best of All!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 4, 'Thy work, the KARMA? Tell me what it is');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 5, 'Thou namest ADHIBHUTA? What again');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 6, 'Means ADHIDAIVA? Yea, and how it comes');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 7, 'Thou canst be ADHIYAJNA in thy flesh?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 8, 'Slayer of Madhu! Further, make me know');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 9, 'How good men find thee in the hour of death?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 10, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 11, 'I BRAHMA am! the One Eternal GOD,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 12, 'And ADHYATMAN is My Being\'s name,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 13, 'The Soul of Souls! What goeth forth from Me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 14, 'Causing all life to live, is KARMA called:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 15, 'And, Manifested in divided forms,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 16, 'I am the ADHIBHUTA, Lord of Lives;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 17, 'And ADHIDAIVA, Lord of all the Gods,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 18, 'Because I am PURUSHA, who begets.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 19, 'And ADHIYAJNA, Lord of Sacrifice,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 20, 'I--speaking with thee in this body here--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 21, 'Am, thou embodied one! (for all the shrines');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 22, 'Flame unto Me!) And, at the hour of death,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 23, 'He that hath meditated Me alone,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 24, 'In putting off his flesh, comes forth to Me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 25, 'Enters into My Being--doubt thou not!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 26, 'But, if he meditated otherwise');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 27, 'At hour of death, in putting off the flesh,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 28, 'He goes to what he looked for, Kunti\'s Son!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 29, 'Because the Soul is fashioned to its like.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 30, 'Have Me, then, in thy heart always! and fight!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 31, 'Thou too, when heart and mind are fixed on Me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 32, 'Shalt surely come to Me! All come who cleave');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 33, 'With never-wavering will of firmest faith,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 34, 'Owning none other Gods: all come to Me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 35, 'The Uttermost, Purusha, Holiest!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 36, 'Whoso hath known Me, Lord of sage and singer,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 37, 'Ancient of days; of all the Three Worlds Stay,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 38, 'Boundless,--but unto every atom Bringer');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 39, 'Of that which quickens it: whoso, I say,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 40, 'Hath known My form, which passeth mortal knowing;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 41, 'Seen my effulgence--which no eye hath seen--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 42, 'Than the sun\'s burning gold more brightly glowing,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 43, 'Dispersing darkness,--unto him hath been');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 44, 'Right life! And, in the hour when life is ending,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 45, 'With mind set fast and trustful piety,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 46, 'Drawing still breath beneath calm brows unbending,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 47, 'In happy peace that faithful one doth die,--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 48, 'In glad peace passeth to Purusha\'s heaven.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 49, 'The place which they who read the Vedas name');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 50, 'AKSHARAM, \"Ultimate;\" whereto have striven');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 51, 'Saints and ascetics--their road is the same.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 52, 'That way--the highest way--goes he who shuts');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 53, 'The gates of all his senses, locks desire');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 54, 'Safe in his heart, centres the vital airs');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 55, 'Upon his parting thought, steadfastly set;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 56, 'And, murmuring OM, the sacred syllable--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 57, 'Emblem of BRAHM--dies, meditating Me.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 58, 'For who, none other Gods regarding, looks');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 59, 'Ever to Me, easily am I gained');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 60, 'By such a Yogi; and, attaining Me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 61, 'They fall not--those Mahatmas--back to birth,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 62, 'To life, which is the place of pain, which ends,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 63, 'But take the way of utmost blessedness.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 64, 'The worlds, Arjuna!--even Brahma\'s world--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 65, 'Roll back again from Death to Life\'s unrest;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 66, 'But they, O Kunti\'s Son! that reach to Me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 67, 'Taste birth no more. If ye know Brahma\'s Day');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 68, 'Which is a thousand Yugas; if ye know');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 69, 'The thousand Yugas making Brahma\'s Night,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 70, 'Then know ye Day and Night as He doth know!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 71, 'When that vast Dawn doth break, th\' Invisible');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 72, 'Is brought anew into the Visible;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 73, 'When that deep Night doth darken, all which is');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 74, 'Fades back again to Him Who sent it forth;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 75, 'Yea! this vast company of living things--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 76, 'Again and yet again produced--expires');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 77, 'At Brahma\'s Nightfall; and, at Brahma\'s Dawn,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 78, 'Riseth, without its will, to life new-born.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 79, 'But--higher, deeper, innermost--abides');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 80, 'Another Life, not like the life of sense,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 81, 'Escaping sight, unchanging. This endures');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 82, 'When all created things have passed away:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 83, 'This is that Life named the Unmanifest,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 84, 'The Infinite! the All! the Uttermost.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 85, 'Thither arriving none return. That Life');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 86, 'Is Mine, and I am there! And, Prince! by faith');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 87, 'Which wanders not, there is a way to come');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 88, 'Thither. I, the PURUSHA, I Who spread');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 89, 'The Universe around me--in Whom dwell');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 90, 'All living Things--may so be reached and seen!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 91, '. . . . . . . . . . . . . . [FN#14]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 92, 'Richer than holy fruit on Vedas growing,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 93, 'Greater than gifts, better than prayer or fast,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 94, 'Such wisdom is! The Yogi, this way knowing,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 95, 'Comes to the Utmost Perfect Peace at last.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 96, 'HERE ENDETH CHAPTER VIII. OF THE BHAGAVAD-GITA,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 97, 'Entitled \"Aksharaparabrahmayog,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 8, 98, 'Or \"The book of Religion by Devotion to the One Supreme God.\"');
INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 9, 'CHAPTER IX', 'Religion by the Kingly Knowledge and the Kingly Mystery');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 1, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 2, 'Now will I open unto thee--whose heart');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 3, 'Rejects not--that last lore, deepest-concealed,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 4, 'That farthest secret of My Heavens and Earths,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 5, 'Which but to know shall set thee free from ills,--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 6, 'A royal lore! a Kingly mystery!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 7, 'Yea! for the soul such light as purgeth it');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 8, 'From every sin; a light of holiness');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 9, 'With inmost splendour shining; plain to see;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 10, 'Easy to walk by, inexhaustible!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 11, 'They that receive not this, failing in faith');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 12, 'To grasp the greater wisdom, reach not Me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 13, 'Destroyer of thy foes! They sink anew');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 14, 'Into the realm of Flesh, where all things change!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 15, 'By Me the whole vast Universe of things');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 16, 'Is spread abroad;--by Me, the Unmanifest!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 17, 'In Me are all existences contained;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 18, 'Not I in them!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 19, 'Yet they are not contained,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 20, 'Those visible things! Receive and strive to embrace');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 21, 'The mystery majestical! My Being--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 22, 'Creating all, sustaining all--still dwells');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 23, 'Outside of all!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 24, 'See! as the shoreless airs');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 25, 'Move in the measureless space, but are not space,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 26, '[And space were space without the moving airs];');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 27, 'So all things are in Me, but are not I.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 28, 'At closing of each Kalpa, Indian Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 29, 'All things which be back to My Being come:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 30, 'At the beginning of each Kalpa, all');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 31, 'Issue new-born from Me.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 32, 'By Energy');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 33, 'And help of Prakriti my outer Self,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 34, 'Again, and yet again, I make go forth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 35, 'The realms of visible things--without their will--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 36, 'All of them--by the power of Prakriti.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 37, 'Yet these great makings, Prince! involve Me not');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 38, 'Enchain Me not! I sit apart from them,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 39, 'Other, and Higher, and Free; nowise attached!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 40, 'Thus doth the stuff of worlds, moulded by Me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 41, 'Bring forth all that which is, moving or still,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 42, 'Living or lifeless! Thus the worlds go on!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 43, 'The minds untaught mistake Me, veiled in form;--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 44, 'Naught see they of My secret Presence, nought');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 45, 'Of My hid Nature, ruling all which lives.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 46, 'Vain hopes pursuing, vain deeds doing; fed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 47, 'On vainest knowledge, senselessly they seek');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 48, 'An evil way, the way of brutes and fiends.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 49, 'But My Mahatmas, those of noble soul');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 50, 'Who tread the path celestial, worship Me');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 51, 'With hearts unwandering,--knowing Me the Source,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 52, 'Th\' Eternal Source, of Life. Unendingly');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 53, 'They glorify Me; seek Me; keep their vows');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 54, 'Of reverence and love, with changeless faith');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 55, 'Adoring Me. Yea, and those too adore,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 56, 'Who, offering sacrifice of wakened hearts,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 57, 'Have sense of one pervading Spirit\'s stress,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 58, 'One Force in every place, though manifold!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 59, 'I am the Sacrifice! I am the Prayer!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 60, 'I am the Funeral-Cake set for the dead!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 61, 'I am the healing herb! I am the ghee,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 62, 'The Mantra, and the flame, and that which burns!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 63, 'I am-of all this boundless Universe-');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 64, 'The Father, Mother, Ancestor, and Guard!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 65, 'The end of Learning! That which purifies');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 66, 'In lustral water! I am OM! I am');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 67, 'Rig-Veda, Sama-Veda, Yajur-Ved;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 68, 'The Way, the Fosterer, the Lord, the Judge,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 69, 'The Witness; the Abode, the Refuge-House,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 70, 'The Friend, the Fountain and the Sea of Life');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 71, 'Which sends, and swallows up; Treasure of Worlds');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 72, 'And Treasure-Chamber! Seed and Seed-Sower,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 73, 'Whence endless harvests spring! Sun\'s heat is mine;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 74, 'Heaven\'s rain is mine to grant or to withhold;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 75, 'Death am I, and Immortal Life I am,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 76, 'Arjuna! SAT and ASAT, Visible Life,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 77, 'And Life Invisible!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 78, 'Yea! those who learn');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 79, 'The threefold Veds, who drink the Soma-wine,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 80, 'Purge sins, pay sacrifice--from Me they earn');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 81, 'Passage to Swarga; where the meats divine');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 82, 'Of great gods feed them in high Indra\'s heaven.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 83, 'Yet they, when that prodigious joy is o\'er,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 84, 'Paradise spent, and wage for merits given,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 85, 'Come to the world of death and change once more.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 86, 'They had their recompense! they stored their treasure,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 87, 'Following the threefold Scripture and its writ;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 88, 'Who seeketh such gaineth the fleeting pleasure');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 89, 'Of joy which comes and goes! I grant them it!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 90, 'But to those blessed ones who worship Me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 91, 'Turning not otherwhere, with minds set fast,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 92, 'I bring assurance of full bliss beyond.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 93, 'Nay, and of hearts which follow other gods');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 94, 'In simple faith, their prayers arise to me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 95, 'O Kunti\'s Son! though they pray wrongfully;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 96, 'For I am the Receiver and the Lord');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 97, 'Of every sacrifice, which these know not');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 98, 'Rightfully; so they fall to earth again!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 99, 'Who follow gods go to their gods; who vow');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 100, 'Their souls to Pitris go to Pitris; minds');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 101, 'To evil Bhuts given o\'er sink to the Bhuts;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 102, 'And whoso loveth Me cometh to Me.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 103, 'Whoso shall offer Me in faith and love');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 104, 'A leaf, a flower, a fruit, water poured forth,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 105, 'That offering I accept, lovingly made');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 106, 'With pious will. Whate\'er thou doest, Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 107, 'Eating or sacrificing, giving gifts,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 108, 'Praying or fasting, let it all be done');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 109, 'For Me, as Mine. So shalt thou free thyself');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 110, 'From Karmabandh, the chain which holdeth men');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 111, 'To good and evil issue, so shalt come');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 112, 'Safe unto Me-when thou art quit of flesh--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 113, 'By faith and abdication joined to Me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 114, 'I am alike for all! I know not hate,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 115, 'I know not favour! What is made is Mine!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 116, 'But them that worship Me with love, I love;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 117, 'They are in Me, and I in them!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 118, 'Nay, Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 119, 'If one of evil life turn in his thought');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 120, 'Straightly to Me, count him amidst the good;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 121, 'He hath the high way chosen; he shall grow');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 122, 'Righteous ere long; he shall attain that peace');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 123, 'Which changes not. Thou Prince of India!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 124, 'Be certain none can perish, trusting Me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 125, 'O Pritha\'s Son! whoso will turn to Me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 126, 'Though they be born from the very womb of Sin,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 127, 'Woman or man; sprung of the Vaisya caste');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 128, 'Or lowly disregarded Sudra,--all');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 129, 'Plant foot upon the highest path; how then');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 130, 'The holy Brahmans and My Royal Saints?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 131, 'Ah! ye who into this ill world are come--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 132, 'Fleeting and false--set your faith fast on Me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 133, 'Fix heart and thought on Me! Adore Me! Bring');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 134, 'Offerings to Me! Make Me prostrations! Make');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 135, 'Me your supremest joy! and, undivided,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 136, 'Unto My rest your spirits shall be guided.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 137, 'HERE ENDS CHAPTER IX. OF THE BHAGAVAD-GITA,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 138, 'Entitled \"Rajavidyarajaguhyayog,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 139, 'Or \"The Book of Religion by the Kingly Knowledge and the Kingly');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 9, 140, 'Mystery.\"');
INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 10, 'CHAPTER X', 'Religion by the Heavenly Perfections');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 1, 'Krishna.[FN#l6]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 2, 'Hear farther yet, thou Long-Armed Lord! these latest words I say--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 3, 'Uttered to bring thee bliss and peace, who lovest Me alway--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 4, 'Not the great company of gods nor kingly Rishis know');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 5, 'My Nature, Who have made the gods and Rishis long ago;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 6, 'He only knoweth-only he is free of sin, and wise,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 7, 'Who seeth Me, Lord of the Worlds, with faith-enlightened eyes,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 8, 'Unborn, undying, unbegun. Whatever Natures be');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 9, 'To mortal men distributed, those natures spring from Me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 10, 'Intellect, skill, enlightenment, endurance, self-control,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 11, 'Truthfulness, equability, and grief or joy of soul,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 12, 'And birth and death, and fearfulness, and fearlessness, and shame,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 13, 'And honour, and sweet harmlessness,[FN#17] and peace which is the');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 14, 'same');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 15, 'Whate\'er befalls, and mirth, and tears, and piety, and thrift,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 16, 'And wish to give, and will to help,--all cometh of My gift!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 17, 'The Seven Chief Saints, the Elders Four, the Lordly Manus set--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 18, 'Sharing My work--to rule the worlds, these too did I beget;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 19, 'And Rishis, Pitris, Manus, all, by one thought of My mind;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 20, 'Thence did arise, to fill this world, the races of mankind;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 21, 'Wherefrom who comprehends My Reign of mystic Majesty--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 22, 'That truth of truths--is thenceforth linked in faultless faith to Me:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 23, 'Yea! knowing Me the source of all, by Me all creatures wrought,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 24, 'The wise in spirit cleave to Me, into My Being brought;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 25, 'Hearts fixed on Me; breaths breathed to Me; praising Me, each to each,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 26, 'So have they happiness and peace, with pious thought and speech;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 27, 'And unto these--thus serving well, thus loving ceaselessly--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 28, 'I give a mind of perfect mood, whereby they draw to Me;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 29, 'And, all for love of them, within their darkened souls I dwell,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 30, 'And, with bright rays of wisdom\'s lamp, their ignorance dispel.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 31, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 32, 'Yes! Thou art Parabrahm! The High Abode!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 33, 'The Great Purification! Thou art God');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 34, 'Eternal, All-creating, Holy, First,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 35, 'Without beginning! Lord of Lords and Gods!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 36, 'Declared by all the Saints--by Narada,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 37, 'Vyasa Asita, and Devalas;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 38, 'And here Thyself declaring unto me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 39, 'What Thou hast said now know I to be truth,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 40, 'O Kesava! that neither gods nor men');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 41, 'Nor demons comprehend Thy mystery');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 42, 'Made manifest, Divinest! Thou Thyself');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 43, 'Thyself alone dost know, Maker Supreme!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 44, 'Master of all the living! Lord of Gods!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 45, 'King of the Universe! To Thee alone');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 46, 'Belongs to tell the heavenly excellence');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 47, 'Of those perfections wherewith Thou dost fill');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 48, 'These worlds of Thine; Pervading, Immanent!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 49, 'How shall I learn, Supremest Mystery!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 50, 'To know Thee, though I muse continually?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 51, 'Under what form of Thine unnumbered forms');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 52, 'Mayst Thou be grasped? Ah! yet again recount,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 53, 'Clear and complete, Thy great appearances,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 54, 'The secrets of Thy Majesty and Might,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 55, 'Thou High Delight of Men! Never enough');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 56, 'Can mine ears drink the Amrit[FN#18] of such words!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 57, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 58, 'Hanta! So be it! Kuru Prince! I will to thee unfold');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 59, 'Some portions of My Majesty, whose powers are manifold!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 60, 'I am the Spirit seated deep in every creature\'s heart;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 61, 'From Me they come; by Me they live; at My word they depart!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 62, 'Vishnu of the Adityas I am, those Lords of Light;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 63, 'Maritchi of the Maruts, the Kings of Storm and Blight;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 64, 'By day I gleam, the golden Sun of burning cloudless Noon;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 65, 'By Night, amid the asterisms I glide, the dappled Moon!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 66, 'Of Vedas I am Sama-Ved, of gods in Indra\'s Heaven');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 67, 'Vasava; of the faculties to living beings given');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 68, 'The mind which apprehends and thinks; of Rudras Sankara;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 69, 'Of Yakshas and of Rakshasas, Vittesh; and Pavaka');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 70, 'Of Vasus, and of mountain-peaks Meru; Vrihaspati');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 71, 'Know Me \'mid planetary Powers; \'mid Warriors heavenly');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 72, 'Skanda; of all the water-floods the Sea which drinketh each,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 73, 'And Bhrigu of the holy Saints, and OM of sacred speech;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 74, 'Of prayers the prayer ye whisper;[FN#19] of hills Himala\'s snow,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 75, 'And Aswattha, the fig-tree, of all the trees that grow;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 76, 'Of the Devarshis, Narada; and Chitrarath of them');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 77, 'That sing in Heaven, and Kapila of Munis, and the gem');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 78, 'Of flying steeds, Uchchaisravas, from Amrit-wave which burst;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 79, 'Of elephants Airavata; of males the Best and First;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 80, 'Of weapons Heav\'n\'s hot thunderbolt; of cows white Kamadhuk,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 81, 'From whose great milky udder-teats all hearts\' desires are strook;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 82, 'Vasuki of the serpent-tribes, round Mandara entwined;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 83, 'And thousand-fanged Ananta, on whose broad coils reclined');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 84, 'Leans Vishnu; and of water-things Varuna; Aryam');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 85, 'Of Pitris, and, of those that judge, Yama the Judge I am;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 86, 'Of Daityas dread Prahlada; of what metes days and years,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 87, 'Time\'s self I am; of woodland-beasts-buffaloes, deers, and bears-');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 88, 'The lordly-painted tiger; of birds the vast Garud,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 89, 'The whirlwind \'mid the winds; \'mid chiefs Rama with blood imbrued,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 90, 'Makar \'mid fishes of the sea, and Ganges \'mid the streams;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 91, 'Yea! First, and Last, and Centre of all which is or seems');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 92, 'I am, Arjuna! Wisdom Supreme of what is wise,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 93, 'Words on the uttering lips I am, and eyesight of the eyes,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 94, 'And \"A\" of written characters, Dwandwa[FN#20] of knitted speech,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 95, 'And Endless Life, and boundless Love, whose power sustaineth each;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 96, 'And bitter Death which seizes all, and joyous sudden Birth,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 97, 'Which brings to light all beings that are to be on earth;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 98, 'And of the viewless virtues, Fame, Fortune, Song am I,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 99, 'And Memory, and Patience; and Craft, and Constancy:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 100, 'Of Vedic hymns the Vrihatsam, of metres Gayatri,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 101, 'Of months the Margasirsha, of all the seasons three');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 102, 'The flower-wreathed Spring; in dicer\'s-play the conquering');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 103, 'Double-Eight;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 104, 'The splendour of the splendid, and the greatness of the great,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 105, 'Victory I am, and Action! and the goodness of the good,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 106, 'And Vasudev of Vrishni\'s race, and of this Pandu brood');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 107, 'Thyself!--Yea, my Arjuna! thyself; for thou art Mine!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 108, 'Of poets Usana, of saints Vyasa, sage divine;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 109, 'The policy of conquerors, the potency of kings,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 110, 'The great unbroken silence in learning\'s secret things;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 111, 'The lore of all the learned, the seed of all which springs.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 112, 'Living or lifeless, still or stirred, whatever beings be,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 113, 'None of them is in all the worlds, but it exists by Me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 114, 'Nor tongue can tell, Arjuna! nor end of telling come');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 115, 'Of these My boundless glories, whereof I teach thee some;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 116, 'For wheresoe\'er is wondrous work, and majesty, and might,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 117, 'From Me hath all proceeded. Receive thou this aright!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 118, 'Yet how shouldst thou receive, O Prince! the vastness of this word?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 119, 'I, who am all, and made it all, abide its separate Lord!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 120, 'HERE ENDETH CHAPTER X. OF THE BHAGAVAD-GITA,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 121, 'Entitled \"Vibhuti Yog,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 10, 122, 'Or \"The Book of Religion by the Heavenly Perfections.\"');
INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 11, 'CHAPTER XI', 'The Manifesting of the One and Manifold');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 1, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 2, 'This, for my soul\'s peace, have I heard from Thee,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 3, 'The unfolding of the Mystery Supreme');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 4, 'Named Adhyatman; comprehending which,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 5, 'My darkness is dispelled; for now I know--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 6, 'O Lotus-eyed![FN#21]--whence is the birth of men,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 7, 'And whence their death, and what the majesties');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 8, 'Of Thine immortal rule. Fain would I see,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 9, 'As thou Thyself declar\'st it, Sovereign Lord!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 10, 'The likeness of that glory of Thy Form');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 11, 'Wholly revealed. O Thou Divinest One!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 12, 'If this can be, if I may bear the sight,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 13, 'Make Thyself visible, Lord of all prayers!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 14, 'Show me Thy very self, the Eternal God!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 15, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 16, 'Gaze, then, thou Son of Pritha! I manifest for thee');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 17, 'Those hundred thousand thousand shapes that clothe my Mystery:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 18, 'I show thee all my semblances, infinite, rich, divine,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 19, 'My changeful hues, my countless forms. See! in this face of mine,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 20, 'Adityas, Vasus, Rudras, Aswins, and Maruts; see');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 21, 'Wonders unnumbered, Indian Prince! revealed to none save thee.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 22, 'Behold! this is the Universe!--Look! what is live and dead');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 23, 'I gather all in one--in Me! Gaze, as thy lips have said,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 24, 'On GOD ETERNAL, VERY GOD! See Me! see what thou prayest!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 25, 'Thou canst not!--nor, with human eyes, Arjuna! ever mayest!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 26, 'Therefore I give thee sense divine. Have other eyes, new light!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 27, 'And, look! This is My glory, unveiled to mortal sight!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 28, 'Sanjaya.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 29, 'Then, O King! the God, so saying,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 30, 'Stood, to Pritha\'s Son displaying');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 31, 'All the splendour, wonder, dread');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 32, 'Of His vast Almighty-head.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 33, 'Out of countless eyes beholding,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 34, 'Out of countless mouths commanding,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 35, 'Countless mystic forms enfolding');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 36, 'In one Form: supremely standing');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 37, 'Countless radiant glories wearing,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 38, 'Countless heavenly weapons bearing,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 39, 'Crowned with garlands of star-clusters,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 40, 'Robed in garb of woven lustres,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 41, 'Breathing from His perfect Presence');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 42, 'Breaths of every subtle essence');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 43, 'Of all heavenly odours; shedding');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 44, 'Blinding brilliance; overspreading--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 45, 'Boundless, beautiful--all spaces');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 46, 'With His all-regarding faces;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 47, 'So He showed! If there should rise');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 48, 'Suddenly within the skies');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 49, 'Sunburst of a thousand suns');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 50, 'Flooding earth with beams undeemed-of,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 51, 'Then might be that Holy One\'s');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 52, 'Majesty and radiance dreamed of!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 53, 'So did Pandu\'s Son behold');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 54, 'All this universe enfold');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 55, 'All its huge diversity');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 56, 'Into one vast shape, and be');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 57, 'Visible, and viewed, and blended');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 58, 'In one Body--subtle, splendid,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 59, 'Nameless--th\' All-comprehending');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 60, 'God of Gods, the Never-Ending');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 61, 'Deity!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 62, 'But, sore amazed,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 63, 'Thrilled, o\'erfilled, dazzled, and dazed,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 64, 'Arjuna knelt; and bowed his head,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 65, 'And clasped his palms; and cried, and said:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 66, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 67, 'Yea! I have seen! I see!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 68, 'Lord! all is wrapped in Thee!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 69, 'The gods are in Thy glorious frame! the creatures');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 70, 'Of earth, and heaven, and hell');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 71, 'In Thy Divine form dwell,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 72, 'And in Thy countenance shine all the features');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 73, 'Of Brahma, sitting lone');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 74, 'Upon His lotus-throne;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 75, 'Of saints and sages, and the serpent races');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 76, 'Ananta, Vasuki;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 77, 'Yea! mightiest Lord! I see');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 78, 'Thy thousand thousand arms, and breasts, and faces,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 79, 'And eyes,--on every side');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 80, 'Perfect, diversified;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 81, 'And nowhere end of Thee, nowhere beginning,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 82, 'Nowhere a centre! Shifts--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 83, 'Wherever soul\'s gaze lifts--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 84, 'Thy central Self, all-wielding, and all-winning!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 85, 'Infinite King! I see');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 86, 'The anadem on Thee,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 87, 'The club, the shell, the discus; see Thee burning');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 88, 'In beams insufferable,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 89, 'Lighting earth, heaven, and hell');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 90, 'With brilliance blazing, glowing, flashing; turning');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 91, 'Darkness to dazzling day,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 92, 'Look I whichever way;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 93, 'Ah, Lord! I worship Thee, the Undivided,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 94, 'The Uttermost of thought,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 95, 'The Treasure-Palace wrought');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 96, 'To hold the wealth of the worlds; the Shield provided');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 97, 'To shelter Virtue\'s laws;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 98, 'The Fount whence Life\'s stream draws');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 99, 'All waters of all rivers of all being:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 100, 'The One Unborn, Unending:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 101, 'Unchanging and Unblending!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 102, 'With might and majesty, past thought, past seeing!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 103, 'Silver of moon and gold');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 104, 'Of sun are glories rolled');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 105, 'From Thy great eyes; Thy visage, beaming tender');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 106, 'Throughout the stars and skies,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 107, 'Doth to warm life surprise');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 108, 'Thy Universe. The worlds are filled with wonder');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 109, 'Of Thy perfections! Space');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 110, 'Star-sprinkled, and void place');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 111, 'From pole to pole of the Blue, from bound to bound,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 112, 'Hath Thee in every spot,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 113, 'Thee, Thee!--Where Thou art not,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 114, 'O Holy, Marvellous Form! is nowhere found!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 115, 'O Mystic, Awful One!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 116, 'At sight of Thee, made known,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 117, 'The Three Worlds quake; the lower gods draw nigh Thee;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 118, 'They fold their palms, and bow');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 119, 'Body, and breast, and brow,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 120, 'And, whispering worship, laud and magnify Thee!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 121, 'Rishis and Siddhas cry');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 122, '\"Hail! Highest Majesty!\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 123, 'From sage and singer breaks the hymn of glory');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 124, 'In dulcet harmony,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 125, 'Sounding the praise of Thee;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 126, 'While countless companies take up the story,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 127, 'Rudras, who ride the storms,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 128, 'Th\' Adityas\' shining forms,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 129, 'Vasus and Sadhyas, Viswas, Ushmapas;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 130, 'Maruts, and those great Twins');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 131, 'The heavenly, fair, Aswins,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 132, 'Gandharvas, Rakshasas, Siddhas, and Asuras,[FN#22]--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 133, 'These see Thee, and revere');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 134, 'In sudden-stricken fear;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 135, 'Yea! the Worlds,--seeing Thee with form stupendous,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 136, 'With faces manifold,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 137, 'With eyes which all behold,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 138, 'Unnumbered eyes, vast arms, members tremendous,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 139, 'Flanks, lit with sun and star,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 140, 'Feet planted near and far,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 141, 'Tushes of terror, mouths wrathful and tender;--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 142, 'The Three wide Worlds before Thee');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 143, 'Adore, as I adore Thee,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 144, 'Quake, as I quake, to witness so much splendour!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 145, 'I mark Thee strike the skies');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 146, 'With front, in wondrous wise');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 147, 'Huge, rainbow-painted, glittering; and thy mouth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 148, 'Opened, and orbs which see');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 149, 'All things, whatever be');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 150, 'In all Thy worlds, east, west, and north and south.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 151, 'O Eyes of God! O Head!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 152, 'My strength of soul is fled,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 153, 'Gone is heart\'s force, rebuked is mind\'s desire!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 154, 'When I behold Thee so,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 155, 'With awful brows a-glow,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 156, 'With burning glance, and lips lighted by fire');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 157, 'Fierce as those flames which shall');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 158, 'Consume, at close of all,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 159, 'Earth, Heaven! Ah me! I see no Earth and Heaven!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 160, 'Thee, Lord of Lords! I see,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 161, 'Thee only-only Thee!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 162, 'Now let Thy mercy unto me be given,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 163, 'Thou Refuge of the World!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 164, 'Lo! to the cavern hurled');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 165, 'Of Thy wide-opened throat, and lips white-tushed,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 166, 'I see our noblest ones,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 167, 'Great Dhritarashtra\'s sons,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 168, 'Bhishma, Drona, and Karna, caught and crushed!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 169, 'The Kings and Chiefs drawn in,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 170, 'That gaping gorge within;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 171, 'The best of both these armies torn and riven!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 172, 'Between Thy jaws they lie');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 173, 'Mangled full bloodily,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 174, 'Ground into dust and death! Like streams down-driven');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 175, 'With helpless haste, which go');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 176, 'In headlong furious flow');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 177, 'Straight to the gulfing deeps of th\' unfilled ocean,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 178, 'So to that flaming cave');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 179, 'Those heroes great and brave');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 180, 'Pour, in unending streams, with helpless motion!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 181, 'Like moths which in the night');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 182, 'Flutter towards a light,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 183, 'Drawn to their fiery doom, flying and dying,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 184, 'So to their death still throng,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 185, 'Blind, dazzled, borne along');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 186, 'Ceaselessly, all those multitudes, wild flying!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 187, 'Thou, that hast fashioned men,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 188, 'Devourest them again,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 189, 'One with another, great and small, alike!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 190, 'The creatures whom Thou mak\'st,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 191, 'With flaming jaws Thou tak\'st,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 192, 'Lapping them up! Lord God! Thy terrors strike');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 193, 'From end to end of earth,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 194, 'Filling life full, from birth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 195, 'To death, with deadly, burning, lurid dread!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 196, 'Ah, Vishnu! make me know');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 197, 'Why is Thy visage so?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 198, 'Who art Thou, feasting thus upon Thy dead?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 199, 'Who? awful Deity!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 200, 'I bow myself to Thee,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 201, 'Namostu Te, Devavara! Prasid![FN#23]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 202, 'O Mightiest Lord! rehearse');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 203, 'Why hast Thou face so fierce?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 204, 'Whence doth this aspect horrible proceed?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 205, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 206, 'Thou seest Me as Time who kills,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 207, 'Time who brings all to doom,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 208, 'The Slayer Time, Ancient of Days, come hither to consume;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 209, 'Excepting thee, of all these hosts of hostile chiefs arrayed,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 210, 'There stands not one shall leave alive the battlefield! Dismayed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 211, 'No longer be! Arise! obtain renown! destroy thy foes!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 212, 'Fight for the kingdom waiting thee when thou hast vanquished those.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 213, 'By Me they fall--not thee! the stroke of death is dealt them now,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 214, 'Even as they show thus gallantly; My instrument art thou!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 215, 'Strike, strong-armed Prince, at Drona! at Bhishma strike! deal death');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 216, 'On Karna, Jyadratha; stay all their warlike breath!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 217, '\'Tis I who bid them perish! Thou wilt but slay the slain;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 218, 'Fight! they must fall, and thou must live, victor upon this plain!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 219, 'Sanjaya.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 220, 'Hearing mighty Keshav\'s word,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 221, 'Tremblingly that helmed Lord');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 222, 'Clasped his lifted palms, and--praying');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 223, 'Grace of Krishna--stood there, saying,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 224, 'With bowed brow and accents broken,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 225, 'These words, timorously spoken:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 226, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 227, 'Worthily, Lord of Might!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 228, 'The whole world hath delight');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 229, 'In Thy surpassing power, obeying Thee;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 230, 'The Rakshasas, in dread');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 231, 'At sight of Thee, are sped');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 232, 'To all four quarters; and the company');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 233, 'Of Siddhas sound Thy name.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 234, 'How should they not proclaim');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 235, 'Thy Majesties, Divinest, Mightiest?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 236, 'Thou Brahm, than Brahma greater!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 237, 'Thou Infinite Creator!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 238, 'Thou God of gods, Life\'s Dwelling-place and Rest!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 239, 'Thou, of all souls the Soul!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 240, 'The Comprehending Whole!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 241, 'Of being formed, and formless being the Framer;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 242, 'O Utmost One! O Lord!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 243, 'Older than eld, Who stored');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 244, 'The worlds with wealth of life! O Treasure-Claimer,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 245, 'Who wottest all, and art');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 246, 'Wisdom Thyself! O Part');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 247, 'In all, and All; for all from Thee have risen');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 248, 'Numberless now I see');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 249, 'The aspects are of Thee!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 250, 'Vayu[FN#24] Thou art, and He who keeps the prison');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 251, 'Of Narak, Yama dark;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 252, 'And Agni\'s shining spark;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 253, 'Varuna\'s waves are Thy waves. Moon and starlight');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 254, 'Are Thine! Prajapati');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 255, 'Art Thou, and \'tis to Thee');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 256, 'They knelt in worshipping the old world\'s far light,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 257, 'The first of mortal men.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 258, 'Again, Thou God! again');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 259, 'A thousand thousand times be magnified!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 260, 'Honour and worship be--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 261, 'Glory and praise,--to Thee');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 262, 'Namo, Namaste, cried on every side;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 263, 'Cried here, above, below,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 264, 'Uttered when Thou dost go,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 265, 'Uttered where Thou dost come! Namo! we call;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 266, 'Namostu! God adored!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 267, 'Namostu! Nameless Lord!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 268, 'Hail to Thee! Praise to Thee! Thou One in all;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 269, 'For Thou art All! Yea, Thou!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 270, 'Ah! if in anger now');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 271, 'Thou shouldst remember I did think Thee Friend,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 272, 'Speaking with easy speech,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 273, 'As men use each to each;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 274, 'Did call Thee \"Krishna,\" \"Prince,\" nor comprehend');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 275, 'Thy hidden majesty,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 276, 'The might, the awe of Thee;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 277, 'Did, in my heedlessness, or in my love,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 278, 'On journey, or in jest,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 279, 'Or when we lay at rest,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 280, 'Sitting at council, straying in the grove,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 281, 'Alone, or in the throng,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 282, 'Do Thee, most Holy! wrong,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 283, 'Be Thy grace granted for that witless sin!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 284, 'For Thou art, now I know,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 285, 'Father of all below,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 286, 'Of all above, of all the worlds within');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 287, 'Guru of Gurus; more');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 288, 'To reverence and adore');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 289, 'Than all which is adorable and high!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 290, 'How, in the wide worlds three');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 291, 'Should any equal be?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 292, 'Should any other share Thy Majesty?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 293, 'Therefore, with body bent');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 294, 'And reverent intent,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 295, 'I praise, and serve, and seek Thee, asking grace.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 296, 'As father to a son,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 297, 'As friend to friend, as one');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 298, 'Who loveth to his lover, turn Thy face');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 299, 'In gentleness on me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 300, 'Good is it I did see');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 301, 'This unknown marvel of Thy Form! But fear');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 302, 'Mingles with joy! Retake,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 303, 'Dear Lord! for pity\'s sake');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 304, 'Thine earthly shape, which earthly eyes may bear!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 305, 'Be merciful, and show');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 306, 'The visage that I know;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 307, 'Let me regard Thee, as of yore, arrayed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 308, 'With disc and forehead-gem,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 309, 'With mace and anadem,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 310, 'Thou that sustainest all things! Undismayed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 311, 'Let me once more behold');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 312, 'The form I loved of old,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 313, 'Thou of the thousand arms and countless eyes!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 314, 'This frightened heart is fain');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 315, 'To see restored again');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 316, 'My Charioteer, in Krishna\'s kind disguise.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 317, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 318, 'Yea! thou hast seen, Arjuna! because I loved thee well,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 319, 'The secret countenance of Me, revealed by mystic spell,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 320, 'Shining, and wonderful, and vast, majestic, manifold,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 321, 'Which none save thou in all the years had favour to behold;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 322, 'For not by Vedas cometh this, nor sacrifice, nor alms,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 323, 'Nor works well-done, nor penance long, nor prayers, nor chaunted');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 324, 'psalms,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 325, 'That mortal eyes should bear to view the Immortal Soul unclad,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 326, 'Prince of the Kurus! This was kept for thee alone! Be glad!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 327, 'Let no more trouble shake thy heart, because thine eyes have seen');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 328, 'My terror with My glory. As I before have been');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 329, 'So will I be again for thee; with lightened heart behold!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 330, 'Once more I am thy Krishna, the form thou knew\'st of old!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 331, 'Sanjaya.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 332, 'These words to Arjuna spake');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 333, 'Vasudev, and straight did take');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 334, 'Back again the semblance dear');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 335, 'Of the well-loved charioteer;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 336, 'Peace and joy it did restore');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 337, 'When the Prince beheld once more');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 338, 'Mighty BRAHMA\'s form and face');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 339, 'Clothed in Krishna\'s gentle grace.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 340, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 341, 'Now that I see come back, Janardana!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 342, 'This friendly human frame, my mind can think');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 343, 'Calm thoughts once more; my heart beats still again!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 344, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 345, 'Yea! it was wonderful and terrible');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 346, 'To view me as thou didst, dear Prince! The gods');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 347, 'Dread and desire continually to view!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 348, 'Yet not by Vedas, nor from sacrifice,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 349, 'Nor penance, nor gift-giving, nor with prayer');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 350, 'Shall any so behold, as thou hast seen!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 351, 'Only by fullest service, perfect faith,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 352, 'And uttermost surrender am I known');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 353, 'And seen, and entered into, Indian Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 354, 'Who doeth all for Me; who findeth Me');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 355, 'In all; adoreth always; loveth all');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 356, 'Which I have made, and Me, for Love\'s sole end');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 357, 'That man, Arjuna! unto Me doth wend.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 358, 'HERE ENDETH CHAPTER XI. OF THE BHAGAVAD-GITA,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 359, 'Entitled \"Viswarupadarsanam,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 11, 360, 'Or \"The Book of the Manifesting of the One and Manifold.\"');
INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 12, 'CHAPTER XII', 'Religion of Faith');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 1, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 2, 'Lord! of the men who serve Thee--true in heart--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 3, 'As God revealed; and of the men who serve,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 4, 'Worshipping Thee Unrevealed, Unbodied, Far,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 5, 'Which take the better way of faith and life?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 6, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 7, 'Whoever serve Me--as I show Myself--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 8, 'Constantly true, in full devotion fixed,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 9, 'Those hold I very holy. But who serve--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 10, 'Worshipping Me The One, The Invisible,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 11, 'The Unrevealed, Unnamed, Unthinkable,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 12, 'Uttermost, All-pervading, Highest, Sure--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 13, 'Who thus adore Me, mastering their sense,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 14, 'Of one set mind to all, glad in all good,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 15, 'These blessed souls come unto Me.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 16, 'Yet, hard');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 17, 'The travail is for such as bend their minds');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 18, 'To reach th\' Unmanifest That viewless path');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 19, 'Shall scarce be trod by man bearing the flesh!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 20, 'But whereso any doeth all his deeds');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 21, 'Renouncing self for Me, full of Me, fixed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 22, 'To serve only the Highest, night and day');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 23, 'Musing on Me--him will I swiftly lift');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 24, 'Forth from life\'s ocean of distress and death,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 25, 'Whose soul clings fast to Me. Cling thou to Me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 26, 'Clasp Me with heart and mind! so shalt thou dwell');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 27, 'Surely with Me on high. But if thy thought');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 28, 'Droops from such height; if thou be\'st weak to set');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 29, 'Body and soul upon Me constantly,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 30, 'Despair not! give Me lower service! seek');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 31, 'To reach Me, worshipping with steadfast will;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 32, 'And, if thou canst not worship steadfastly,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 33, 'Work for Me, toil in works pleasing to Me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 34, 'For he that laboureth right for love of Me');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 35, 'Shall finally attain! But, if in this');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 36, 'Thy faint heart fails, bring Me thy failure! find');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 37, 'Refuge in Me! let fruits of labour go,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 38, 'Renouncing hope for Me, with lowliest heart,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 39, 'So shalt thou come; for, though to know is more');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 40, 'Than diligence, yet worship better is');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 41, 'Than knowing, and renouncing better still.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 42, 'Near to renunciation--very near--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 43, 'Dwelleth Eternal Peace!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 44, 'Who hateth nought');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 45, 'Of all which lives, living himself benign,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 46, 'Compassionate, from arrogance exempt,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 47, 'Exempt from love of self, unchangeable');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 48, 'By good or ill; patient, contented, firm');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 49, 'In faith, mastering himself, true to his word,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 50, 'Seeking Me, heart and soul; vowed unto Me,--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 51, 'That man I love! Who troubleth not his kind,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 52, 'And is not troubled by them; clear of wrath,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 53, 'Living too high for gladness, grief, or fear,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 54, 'That man I love! Who, dwelling quiet-eyed,[FN#25]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 55, 'Stainless, serene, well-balanced, unperplexed,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 56, 'Working with Me, yet from all works detached,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 57, 'That man I love! Who, fixed in faith on Me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 58, 'Dotes upon none, scorns none; rejoices not,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 59, 'And grieves not, letting good or evil hap');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 60, 'Light when it will, and when it will depart,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 61, 'That man I love! Who, unto friend and foe');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 62, 'Keeping an equal heart, with equal mind');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 63, 'Bears shame and glory; with an equal peace');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 64, 'Takes heat and cold, pleasure and pain; abides');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 65, 'Quit of desires, hears praise or calumny');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 66, 'In passionless restraint, unmoved by each;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 67, 'Linked by no ties to earth, steadfast in Me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 68, 'That man I love! But most of all I love');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 69, 'Those happy ones to whom \'tis life to live');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 70, 'In single fervid faith and love unseeing,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 71, 'Drinking the blessed Amrit of my Being!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 72, 'HERE ENDETH CHAPTER XII. OF THE BHAGAVAD-GITA,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 73, 'Entitled \"Bhaktiyog,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 12, 74, 'Or\"The Book of the Religion of Faith.\"');
INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 13, 'CHAPTER XIII', 'Religion by Separation of Matter and Spirit');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 1, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 2, 'Now would I hear, O gracious Kesava![FN#26]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 3, 'Of Life which seems, and Soul beyond, which sees,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 4, 'And what it is we know-or think to know.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 5, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 6, 'Yea! Son of Kunti! for this flesh ye see');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 7, 'Is Kshetra, is the field where Life disports;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 8, 'And that which views and knows it is the Soul,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 9, 'Kshetrajna. In all \"fields,\" thou Indian prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 10, 'I am Kshetrajna. I am what surveys!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 11, 'Only that knowledge knows which knows the known');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 12, 'By the knower![FN#27] What it is, that \"field\" of life,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 13, 'What qualities it hath, and whence it is,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 14, 'And why it changeth, and the faculty');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 15, 'That wotteth it, the mightiness of this,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 16, 'And how it wotteth-hear these things from Me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 17, '. . . . . . . . . . . .[FN#28]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 18, 'The elements, the conscious life, the mind,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 19, 'The unseen vital force, the nine strange gates');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 20, 'Of the body, and the five domains of sense;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 21, 'Desire, dislike, pleasure and pain, and thought');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 22, 'Deep-woven, and persistency of being;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 23, 'These all are wrought on Matter by the Soul!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 24, 'Humbleness, truthfulness, and harmlessness,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 25, 'Patience and honour, reverence for the wise.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 26, 'Purity, constancy, control of self,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 27, 'Contempt of sense-delights, self-sacrifice,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 28, 'Perception of the certitude of ill');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 29, 'In birth, death, age, disease, suffering, and sin;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 30, 'Detachment, lightly holding unto home,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 31, 'Children, and wife, and all that bindeth men;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 32, 'An ever-tranquil heart in fortunes good');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 33, 'And fortunes evil, with a will set firm');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 34, 'To worship Me--Me only! ceasing not;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 35, 'Loving all solitudes, and shunning noise');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 36, 'Of foolish crowds; endeavours resolute');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 37, 'To reach perception of the Utmost Soul,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 38, 'And grace to understand what gain it were');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 39, 'So to attain,--this is true Wisdom, Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 40, 'And what is otherwise is ignorance!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 41, 'Now will I speak of knowledge best to know-');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 42, 'That Truth which giveth man Amrit to drink,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 43, 'The Truth of HIM, the Para-Brahm, the All,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 44, 'The Uncreated;; not Asat, not Sat,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 45, 'Not Form, nor the Unformed; yet both, and more;--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 46, 'Whose hands are everywhere, and everywhere');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 47, 'Planted His feet, and everywhere His eyes');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 48, 'Beholding, and His ears in every place');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 49, 'Hearing, and all His faces everywhere');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 50, 'Enlightening and encompassing His worlds.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 51, 'Glorified in the senses He hath given,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 52, 'Yet beyond sense He is; sustaining all,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 53, 'Yet dwells He unattached: of forms and modes');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 54, 'Master, yet neither form nor mode hath He;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 55, 'He is within all beings--and without--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 56, 'Motionless, yet still moving; not discerned');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 57, 'For subtlety of instant presence; close');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 58, 'To all, to each; yet measurelessly far!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 59, 'Not manifold, and yet subsisting still');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 60, 'In all which lives; for ever to be known');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 61, 'As the Sustainer, yet, at the End of Times,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 62, 'He maketh all to end--and re-creates.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 63, 'The Light of Lights He is, in the heart of the Dark');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 64, 'Shining eternally. Wisdom He is');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 65, 'And Wisdom\'s way, and Guide of all the wise,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 66, 'Planted in every heart.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 67, 'So have I told');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 68, 'Of Life\'s stuff, and the moulding, and the lore');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 69, 'To comprehend. Whoso, adoring Me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 70, 'Perceiveth this, shall surely come to Me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 71, 'Know thou that Nature and the Spirit both');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 72, 'Have no beginning! Know that qualities');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 73, 'And changes of them are by Nature wrought;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 74, 'That Nature puts to work the acting frame,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 75, 'But Spirit doth inform it, and so cause');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 76, 'Feeling of pain and pleasure. Spirit, linked');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 77, 'To moulded matter, entereth into bond');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 78, 'With qualities by Nature framed, and, thus');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 79, 'Married to matter, breeds the birth again');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 80, 'In good or evil yonis.[FN#29]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 81, 'Yet is this');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 82, 'Yea! in its bodily prison!--Spirit pure,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 83, 'Spirit supreme; surveying, governing,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 84, 'Guarding, possessing; Lord and Master still');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 85, 'PURUSHA, Ultimate, One Soul with Me.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 86, 'Whoso thus knows himself, and knows his soul');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 87, 'PURUSHA, working through the qualities');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 88, 'With Nature\'s modes, the light hath come for him!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 89, 'Whatever flesh he bears, never again');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 90, 'Shall he take on its load. Some few there be');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 91, 'By meditation find the Soul in Self');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 92, 'Self-schooled; and some by long philosophy');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 93, 'And holy life reach thither; some by works:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 94, 'Some, never so attaining, hear of light');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 95, 'From other lips, and seize, and cleave to it');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 96, 'Worshipping; yea! and those--to teaching true--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 97, 'Overpass Death!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 98, 'Wherever, Indian Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 99, 'Life is--of moving things, or things unmoved,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 100, 'Plant or still seed--know, what is there hath grown');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 101, 'By bond of Matter and of Spirit: Know');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 102, 'He sees indeed who sees in all alike');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 103, 'The living, lordly Soul; the Soul Supreme,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 104, 'Imperishable amid the Perishing:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 105, 'For, whoso thus beholds, in every place,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 106, 'In every form, the same, one, Living Life,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 107, 'Doth no more wrongfulness unto himself,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 108, 'But goes the highest road which brings to bliss.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 109, 'Seeing, he sees, indeed, who sees that works');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 110, 'Are Nature\'s wont, for Soul to practise by');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 111, 'Acting, yet not the agent; sees the mass');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 112, 'Of separate living things--each of its kind--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 113, 'Issue from One, and blend again to One:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 114, 'Then hath he BRAHMA, he attains!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 115, 'O Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 116, 'That Ultimate, High Spirit, Uncreate,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 117, 'Unqualified, even when it entereth flesh');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 118, 'Taketh no stain of acts, worketh in nought!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 119, 'Like to the ethereal air, pervading all,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 120, 'Which, for sheer subtlety, avoideth taint,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 121, 'The subtle Soul sits everywhere, unstained:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 122, 'Like to the light of the all-piercing sun');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 123, '[Which is not changed by aught it shines upon,]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 124, 'The Soul\'s light shineth pure in every place;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 125, 'And they who, by such eye of wisdom, see');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 126, 'How Matter, and what deals with it, divide;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 127, 'And how the Spirit and the flesh have strife,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 128, 'Those wise ones go the way which leads to Life!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 129, 'HERE ENDS CHAPTER XIII. OF THE BHAGAVAD-GITA,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 130, 'Entitled \"Kshetrakshetrajnavibhagayog,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 13, 131, 'Or \"The Book of Religion by Separation of Matter and Spirit.\"');
INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 14, 'CHAPTER XIV', 'Religion by Separation from the Qualities');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 1, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 2, 'Yet farther will I open unto thee');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 3, 'This wisdom of all wisdoms, uttermost,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 4, 'The which possessing, all My saints have passed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 5, 'To perfectness. On such high verities');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 6, 'Reliant, rising into fellowship');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 7, 'With Me, they are not born again at birth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 8, 'Of Kalpas, nor at Pralyas suffer change!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 9, 'This Universe the womb is where I plant');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 10, 'Seed of all lives! Thence, Prince of India, comes');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 11, 'Birth to all beings! Whoso, Kunti\'s Son!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 12, 'Mothers each mortal form, Brahma conceives,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 13, 'And I am He that fathers, sending seed!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 14, 'Sattwan, Rajas, and Tamas, so are named');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 15, 'The qualities of Nature, \"Soothfastness,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 16, '\"Passion,\" and \"Ignorance.\" These three bind down');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 17, 'The changeless Spirit in the changeful flesh.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 18, 'Whereof sweet \"Soothfastness,\" by purity');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 19, 'Living unsullied and enlightened, binds');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 20, 'The sinless Soul to happiness and truth;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 21, 'And Passion, being kin to appetite,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 22, 'And breeding impulse and propensity,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 23, 'Binds the embodied Soul, O Kunti\'s Son!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 24, 'By tie of works. But Ignorance, begot');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 25, 'Of Darkness, blinding mortal men, binds down');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 26, 'Their souls to stupor, sloth, and drowsiness.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 27, 'Yea, Prince of India! Soothfastness binds souls');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 28, 'In pleasant wise to flesh; and Passion binds');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 29, 'By toilsome strain; but Ignorance, which blots');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 30, 'The beams of wisdom, binds the soul to sloth.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 31, 'Passion and Ignorance, once overcome,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 32, 'Leave Soothfastness, O Bharata! Where this');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 33, 'With Ignorance are absent, Passion rules;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 34, 'And Ignorance in hearts not good nor quick.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 35, 'When at all gateways of the Body shines');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 36, 'The Lamp of Knowledge, then may one see well');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 37, 'Soothfastness settled in that city reigns;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 38, 'Where longing is, and ardour, and unrest,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 39, 'Impulse to strive and gain, and avarice,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 40, 'Those spring from Passion--Prince!--engrained; and where');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 41, 'Darkness and dulness, sloth and stupor are,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 42, '\'Tis Ignorance hath caused them, Kuru Chief!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 43, 'Moreover, when a soul departeth, fixed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 44, 'In Soothfastness, it goeth to the place--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 45, 'Perfect and pure--of those that know all Truth.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 46, 'If it departeth in set habitude');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 47, 'Of Impulse, it shall pass into the world');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 48, 'Of spirits tied to works; and, if it dies');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 49, 'In hardened Ignorance, that blinded soul');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 50, 'Is born anew in some unlighted womb.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 51, 'The fruit of Soothfastness is true and sweet;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 52, 'The fruit of lusts is pain and toil; the fruit');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 53, 'Of Ignorance is deeper darkness. Yea!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 54, 'For Light brings light, and Passion ache to have;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 55, 'And gloom, bewilderments, and ignorance');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 56, 'Grow forth from Ignorance. Those of the first');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 57, 'Rise ever higher; those of the second mode');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 58, 'Take a mid place; the darkened souls sink back');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 59, 'To lower deeps, loaded with witlessness!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 60, 'When, watching life, the living man perceives');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 61, 'The only actors are the Qualities,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 62, 'And knows what rules beyond the Qualities,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 63, 'Then is he come nigh unto Me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 64, 'The Soul,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 65, 'Thus passing forth from the Three Qualities--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 66, 'Whereby arise all bodies--overcomes');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 67, 'Birth, Death, Sorrow, and Age; and drinketh deep');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 68, 'The undying wine of Amrit.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 69, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 70, 'Oh, my Lord!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 71, 'Which be the signs to know him that hath gone');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 72, 'Past the Three Modes? How liveth he? What way');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 73, 'Leadeth him safe beyond the threefold Modes?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 74, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 75, 'He who with equanimity surveys');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 76, 'Lustre of goodness, strife of passion, sloth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 77, 'Of ignorance, not angry if they are,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 78, 'Not wishful when they are not: he who sits');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 79, 'A sojourner and stranger in their midst');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 80, 'Unruffled, standing off, saying--serene--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 81, 'When troubles break, \"These be the Qualities!\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 82, 'He unto whom--self-centred--grief and joy');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 83, 'Sound as one word; to whose deep-seeing eyes');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 84, 'The clod, the marble, and the gold are one;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 85, 'Whose equal heart holds the same gentleness');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 86, 'For lovely and unlovely things, firm-set,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 87, 'Well-pleased in praise and dispraise; satisfied');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 88, 'With honour or dishonour; unto friends');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 89, 'And unto foes alike in tolerance;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 90, 'Detached from undertakings,--he is named');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 91, 'Surmounter of the Qualities!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 92, 'And such--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 93, 'With single, fervent faith adoring Me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 94, 'Passing beyond the Qualities, conforms');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 95, 'To Brahma, and attains Me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 96, 'For I am');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 97, 'That whereof Brahma is the likeness! Mine');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 98, 'The Amrit is; and Immortality');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 99, 'Is mine; and mine perfect Felicity!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 100, 'HERE ENDS CHAPTER XIV. OF THE BHAGAVAD-GITA');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 101, 'Entitled \"Gunatrayavibhagayog,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 14, 102, 'Or \"The Book of Religion by Separation from the Qualities.\"');
INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 15, 'CHAPTER XV', 'Religion by Attaining the Supreme');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 1, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 2, 'Men call the Aswattha,--the Banyan-tree,--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 3, 'Which hath its boughs beneath, its roots above,--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 4, 'The ever-holy tree. Yea! for its leaves');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 5, 'Are green and waving hymns which whisper Truth!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 6, 'Who knows the Aswattha, knows Veds, and all.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 7, 'Its branches shoot to heaven and sink to earth,[FN#30]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 8, 'Even as the deeds of men, which take their birth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 9, 'From qualities: its silver sprays and blooms,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 10, 'And all the eager verdure of its girth,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 11, 'Leap to quick life at kiss of sun and air,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 12, 'As men\'s lives quicken to the temptings fair');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 13, 'Of wooing sense: its hanging rootlets seek');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 14, 'The soil beneath, helping to hold it there,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 15, 'As actions wrought amid this world of men');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 16, 'Bind them by ever-tightening bonds again.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 17, 'If ye knew well the teaching of the Tree,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 18, 'What its shape saith; and whence it springs; and, then');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 19, 'How it must end, and all the ills of it,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 20, 'The axe of sharp Detachment ye would whet,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 21, 'And cleave the clinging snaky roots, and lay');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 22, 'This Aswattha of sense-life low,--to set');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 23, 'New growths upspringing to that happier sky,--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 24, 'Which they who reach shall have no day to die,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 25, 'Nor fade away, nor fall--to Him, I mean,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 26, 'FATHER and FIRST, Who made the mystery');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 27, 'Of old Creation; for to Him come they');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 28, 'From passion and from dreams who break away;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 29, 'Who part the bonds constraining them to flesh,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 30, 'And,--Him, the Highest, worshipping alway--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 31, 'No longer grow at mercy of what breeze');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 32, 'Of summer pleasure stirs the sleeping trees,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 33, 'What blast of tempest tears them, bough and stem');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 34, 'To the eternal world pass such as these!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 35, 'Another Sun gleams there! another Moon!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 36, 'Another Light,--not Dusk, nor Dawn, nor Noon--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 37, 'Which they who once behold return no more;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 38, 'They have attained My rest, life\'s Utmost boon!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 39, 'When, in this world of manifested life,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 40, 'The undying Spirit, setting forth from Me,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 41, 'Taketh on form, it draweth to itself');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 42, 'From Being\'s storehouse,--which containeth all,--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 43, 'Senses and intellect. The Sovereign Soul');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 44, 'Thus entering the flesh, or quitting it,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 45, 'Gathers these up, as the wind gathers scents,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 46, 'Blowing above the flower-beds. Ear and Eye,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 47, 'And Touch and Taste, and Smelling, these it takes,--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 48, 'Yea, and a sentient mind;--linking itself');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 49, 'To sense-things so.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 50, 'The unenlightened ones');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 51, 'Mark not that Spirit when he goes or comes,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 52, 'Nor when he takes his pleasure in the form,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 53, 'Conjoined with qualities; but those see plain');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 54, 'Who have the eyes to see. Holy souls see');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 55, 'Which strive thereto. Enlightened, they perceive');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 56, 'That Spirit in themselves; but foolish ones,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 57, 'Even though they strive, discern not, having hearts');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 58, 'Unkindled, ill-informed!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 59, 'Know, too, from Me');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 60, 'Shineth the gathered glory of the suns');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 61, 'Which lighten all the world: from Me the moons');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 62, 'Draw silvery beams, and fire fierce loveliness.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 63, 'I penetrate the clay, and lend all shapes');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 64, 'Their living force; I glide into the plant--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 65, 'Root, leaf, and bloom--to make the woodlands green');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 66, 'With springing sap. Becoming vital warmth,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 67, 'I glow in glad, respiring frames, and pass,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 68, 'With outward and with inward breath, to feed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 69, 'The body by all meats.[FN#31]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 70, 'For in this world');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 71, 'Being is twofold: the Divided, one;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 72, 'The Undivided, one. All things that live');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 73, 'Are \"the Divided.\" That which sits apart,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 74, '\"The Undivided.\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 75, 'Higher still is He,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 76, 'The Highest, holding all, whose Name is LORD,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 77, 'The Eternal, Sovereign, First! Who fills all worlds,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 78, 'Sustaining them. And--dwelling thus beyond');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 79, 'Divided Being and Undivided--I');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 80, 'Am called of men and Vedas, Life Supreme,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 81, 'The PURUSHOTTAMA.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 82, 'Who knows Me thus,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 83, 'With mind unclouded, knoweth all, dear Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 84, 'And with his whole soul ever worshippeth Me.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 85, 'Now is the sacred, secret Mystery');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 86, 'Declared to thee! Who comprehendeth this');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 87, 'Hath wisdom! He is quit of works in bliss!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 88, 'HERE ENDS CHAPTER XV. OF THE BHAGAVAD-GITA');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 89, 'Entitled \"Purushottamapraptiyog,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 15, 90, 'Or \"The Book of Religion by attaining the Supreme.\"');
INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 16, 'CHAPTER XVI', 'The Separateness of the Divine and Undivine');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 1, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 2, 'Fearlessness, singleness of soul, the will');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 3, 'Always to strive for wisdom; opened hand');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 4, 'And governed appetites; and piety,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 5, 'And love of lonely study; humbleness,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 6, 'Uprightness, heed to injure nought which lives,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 7, 'Truthfulness, slowness unto wrath, a mind');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 8, 'That lightly letteth go what others prize;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 9, 'And equanimity, and charity');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 10, 'Which spieth no man\'s faults; and tenderness');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 11, 'Towards all that suffer; a contented heart,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 12, 'Fluttered by no desires; a bearing mild,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 13, 'Modest, and grave, with manhood nobly mixed,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 14, 'With patience, fortitude, and purity;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 15, 'An unrevengeful spirit, never given');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 16, 'To rate itself too high;--such be the signs,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 17, 'O Indian Prince! of him whose feet are set');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 18, 'On that fair path which leads to heavenly birth!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 19, 'Deceitfulness, and arrogance, and pride,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 20, 'Quickness to anger, harsh and evil speech,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 21, 'And ignorance, to its own darkness blind,--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 22, 'These be the signs, My Prince! of him whose birth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 23, 'Is fated for the regions of the vile.[FN#32]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 24, 'The Heavenly Birth brings to deliverance,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 25, 'So should\'st thou know! The birth with Asuras');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 26, 'Brings into bondage. Be thou joyous, Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 27, 'Whose lot is set apart for heavenly Birth.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 28, 'Two stamps there are marked on all living men,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 29, 'Divine and Undivine; I spake to thee');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 30, 'By what marks thou shouldst know the Heavenly Man,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 31, 'Hear from me now of the Unheavenly!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 32, 'They comprehend not, the Unheavenly,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 33, 'How Souls go forth from Me; nor how they come');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 34, 'Back unto Me: nor is there Truth in these,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 35, 'Nor purity, nor rule of Life. \"This world');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 36, 'Hath not a Law, nor Order, nor a Lord,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 37, 'So say they: \"nor hath risen up by Cause');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 38, 'Following on Cause, in perfect purposing,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 39, 'But is none other than a House of Lust.\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 40, 'And, this thing thinking, all those ruined ones--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 41, 'Of little wit, dark-minded--give themselves');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 42, 'To evil deeds, the curses of their kind.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 43, 'Surrendered to desires insatiable,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 44, 'Full of deceitfulness, folly, and pride,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 45, 'In blindness cleaving to their errors, caught');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 46, 'Into the sinful course, they trust this lie');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 47, 'As it were true--this lie which leads to death--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 48, 'Finding in Pleasure all the good which is,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 49, 'And crying \"Here it finisheth!\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 50, 'Ensnared');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 51, 'In nooses of a hundred idle hopes,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 52, 'Slaves to their passion and their wrath, they buy');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 53, 'Wealth with base deeds, to glut hot appetites;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 54, '\"Thus much, to-day,\" they say, \"we gained! thereby');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 55, 'Such and such wish of heart shall have its fill;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 56, 'And this is ours! and th\' other shall be ours!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 57, 'To-day we slew a foe, and we will slay');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 58, 'Our other enemy to-morrow! Look!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 59, 'Are we not lords? Make we not goodly cheer?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 60, 'Is not our fortune famous, brave, and great?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 61, 'Rich are we, proudly born! What other men');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 62, 'Live like to us? Kill, then, for sacrifice!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 63, 'Cast largesse, and be merry!\" So they speak');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 64, 'Darkened by ignorance; and so they fall--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 65, 'Tossed to and fro with projects, tricked, and bound');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 66, 'In net of black delusion, lost in lusts--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 67, 'Down to foul Naraka. Conceited, fond,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 68, 'Stubborn and proud, dead-drunken with the wine');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 69, 'Of wealth, and reckless, all their offerings');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 70, 'Have but a show of reverence, being not made');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 71, 'In piety of ancient faith. Thus vowed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 72, 'To self-hood, force, insolence, feasting, wrath,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 73, 'These My blasphemers, in the forms they wear');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 74, 'And in the forms they breed, my foemen are,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 75, 'Hateful and hating; cruel, evil, vile,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 76, 'Lowest and least of men, whom I cast down');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 77, 'Again, and yet again, at end of lives,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 78, 'Into some devilish womb, whence--birth by birth--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 79, 'The devilish wombs re-spawn them, all beguiled;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 80, 'And, till they find and worship Me, sweet Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 81, 'Tread they that Nether Road.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 82, 'The Doors of Hell');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 83, 'Are threefold, whereby men to ruin pass,--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 84, 'The door of Lust, the door of Wrath, the door');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 85, 'Of Avarice. Let a man shun those three!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 86, 'He who shall turn aside from entering');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 87, 'All those three gates of Narak, wendeth straight');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 88, 'To find his peace, and comes to Swarga\'s gate.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 89, '. . . . . . . . . . . .[FN#33]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 90, 'HERE ENDETH CHAPTER XVI. OF THE BHAGAVAD-GITA,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 91, 'Entitled \"Daivasarasaupadwibhagayog,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 16, 92, 'Or \"The Book of the Separateness of the Divine and Undivine.\"');
INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 17, 'CHAPTER XVII', 'Religion by the Threefold Faith');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 1, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 2, 'If men forsake the holy ordinance,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 3, 'Heedless of Shastras, yet keep faith at heart');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 4, 'And worship, what shall be the state of those,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 5, 'Great Krishna! Sattwan, Rajas, Tamas? Say!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 6, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 7, 'Threefold the faith is of mankind and springs');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 8, 'From those three qualities,--becoming \"true,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 9, 'Or \"passion-stained,\" or \"dark,\" as thou shalt hear!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 10, 'The faith of each believer, Indian Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 11, 'Conforms itself to what he truly is.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 12, 'Where thou shalt see a worshipper, that one');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 13, 'To what he worships lives assimilate,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 14, '[Such as the shrine, so is the votary,]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 15, 'The \"soothfast\" souls adore true gods; the souls');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 16, 'Obeying Rajas worship Rakshasas[FN#34]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 17, 'Or Yakshas; and the men of Darkness pray');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 18, 'To Pretas and to Bhutas.[FN#35] Yea, and those');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 19, 'Who practise bitter penance, not enjoined');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 20, 'By rightful rule--penance which hath its root');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 21, 'In self-sufficient, proud hypocrisies--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 22, 'Those men, passion-beset, violent, wild,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 23, 'Torturing--the witless ones--My elements');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 24, 'Shut in fair company within their flesh,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 25, '(Nay, Me myself, present within the flesh!)');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 26, 'Know them to devils devoted, not to Heaven!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 27, 'For like as foods are threefold for mankind');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 28, 'In nourishing, so is there threefold way');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 29, 'Of worship, abstinence, and almsgiving!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 30, 'Hear this of Me! there is a food which brings');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 31, 'Force, substance, strength, and health, and joy to live,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 32, 'Being well-seasoned, cordial, comforting,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 33, 'The \"Soothfast\" meat. And there be foods which bring');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 34, 'Aches and unrests, and burning blood, and grief,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 35, 'Being too biting, heating, salt, and sharp,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 36, 'And therefore craved by too strong appetite.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 37, 'And there is foul food--kept from over-night,[FN#36]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 38, 'Savourless, filthy, which the foul will eat,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 39, 'A feast of rottenness, meet for the lips');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 40, 'Of such as love the \"Darkness.\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 41, 'Thus with rites;--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 42, 'A sacrifice not for rewardment made,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 43, 'Offered in rightful wise, when he who vows');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 44, 'Sayeth, with heart devout, \"This I should do!\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 45, 'Is \"Soothfast\" rite. But sacrifice for gain,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 46, 'Offered for good repute, be sure that this,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 47, 'O Best of Bharatas! is Rajas-rite,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 48, 'With stamp of \"passion.\" And a sacrifice');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 49, 'Offered against the laws, with no due dole');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 50, 'Of food-giving, with no accompaniment');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 51, 'Of hallowed hymn, nor largesse to the priests,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 52, 'In faithless celebration, call it vile,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 53, 'The deed of \"Darkness!\"--lost!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 54, 'Worship of gods');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 55, 'Meriting worship; lowly reverence');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 56, 'Of Twice-borns, Teachers, Elders; Purity,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 57, 'Rectitude, and the Brahmacharya\'s vow,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 58, 'And not to injure any helpless thing,--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 59, 'These make a true religiousness of Act.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 60, 'Words causing no man woe, words ever true,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 61, 'Gentle and pleasing words, and those ye say');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 62, 'In murmured reading of a Sacred Writ,--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 63, 'These make the true religiousness of Speech.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 64, 'Serenity of soul, benignity,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 65, 'Sway of the silent Spirit, constant stress');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 66, 'To sanctify the Nature,--these things make');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 67, 'Good rite, and true religiousness of Mind.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 68, 'Such threefold faith, in highest piety');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 69, 'Kept, with no hope of gain, by hearts devote,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 70, 'Is perfect work of Sattwan, true belief.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 71, 'Religion shown in act of proud display');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 72, 'To win good entertainment, worship, fame,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 73, 'Such--say I--is of Rajas, rash and vain.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 74, 'Religion followed by a witless will');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 75, 'To torture self, or come at power to hurt');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 76, 'Another,--\'tis of Tamas, dark and ill.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 77, 'The gift lovingly given, when one shall say');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 78, '\"Now must I gladly give!\" when he who takes');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 79, 'Can render nothing back; made in due place,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 80, 'Due time, and to a meet recipient,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 81, 'Is gift of Sattwan, fair and profitable.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 82, 'The gift selfishly given, where to receive');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 83, 'Is hoped again, or when some end is sought,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 84, 'Or where the gift is proffered with a grudge,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 85, 'This is of Rajas, stained with impulse, ill.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 86, 'The gift churlishly flung, at evil time,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 87, 'In wrongful place, to base recipient,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 88, 'Made in disdain or harsh unkindliness,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 89, 'Is gift of Tamas, dark; it doth not bless![FN#37]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 90, 'HERE ENDETH CHAPTER XVII. OF THE BHAGAVAD-GITA,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 91, 'Entitled \"Sraddhatrayavibhagayog,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 17, 92, 'Or \"The Book of Religion by the Threefold Kinds of Faith.\"');
INSERT INTO chapter (book_id, translation_id, chapter_number, name, title) VALUES (119, 220, 18, 'CHAPTER XVIII', 'Religion by Deliverance and Renunciation');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 1, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 2, 'Fain would I better know, Thou Glorious One!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 3, 'The very truth--Heart\'s Lord!--of Sannyas,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 4, 'Abstention; and enunciation, Lord!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 5, 'Tyaga; and what separates these twain!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 6, 'Krishna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 7, 'The poets rightly teach that Sannyas');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 8, 'Is the foregoing of all acts which spring');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 9, 'Out of desire; and their wisest say');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 10, 'Tyaga is renouncing fruit of acts.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 11, 'There be among the saints some who have held');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 12, 'All action sinful, and to be renounced;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 13, 'And some who answer, \"Nay! the goodly acts--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 14, 'As worship, penance, alms--must be performed!\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 15, 'Hear now My sentence, Best of Bharatas!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 16, '\'Tis well set forth, O Chaser of thy Foes!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 17, 'Renunciation is of threefold form,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 18, 'And Worship, Penance, Alms, not to be stayed;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 19, 'Nay, to be gladly done; for all those three');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 20, 'Are purifying waters for true souls!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 21, 'Yet must be practised even those high works');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 22, 'In yielding up attachment, and all fruit');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 23, 'Produced by works. This is My judgment, Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 24, 'This My insuperable and fixed decree!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 25, 'Abstaining from a work by right prescribed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 26, 'Never is meet! So to abstain doth spring');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 27, 'From \"Darkness,\" and Delusion teacheth it.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 28, 'Abstaining from a work grievous to flesh,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 29, 'When one saith \"\'Tis unpleasing!\" this is null!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 30, 'Such an one acts from \"passion;\" nought of gain');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 31, 'Wins his Renunciation! But, Arjun!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 32, 'Abstaining from attachment to the work,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 33, 'Abstaining from rewardment in the work,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 34, 'While yet one doeth it full faithfully,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 35, 'Saying, \"Tis right to do!\" that is \"true \" act');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 36, 'And abstinence! Who doeth duties so,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 37, 'Unvexed if his work fail, if it succeed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 38, 'Unflattered, in his own heart justified,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 39, 'Quit of debates and doubts, his is \"true\" act:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 40, 'For, being in the body, none may stand');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 41, 'Wholly aloof from act; yet, who abstains');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 42, 'From profit of his acts is abstinent.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 43, 'The fruit of labours, in the lives to come,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 44, 'Is threefold for all men,--Desirable,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 45, 'And Undesirable, and mixed of both;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 46, 'But no fruit is at all where no work was.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 47, 'Hear from me, Long-armed Lord! the makings five');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 48, 'Which go to every act, in Sankhya taught');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 49, 'As necessary. First the force; and then');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 50, 'The agent; next, the various instruments;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 51, 'Fourth, the especial effort; fifth, the God.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 52, 'What work soever any mortal doth');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 53, 'Of body, mind, or speech, evil or good,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 54, 'By these five doth he that. Which being thus,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 55, 'Whoso, for lack of knowledge, seeth himself');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 56, 'As the sole actor, knoweth nought at all');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 57, 'And seeth nought. Therefore, I say, if one--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 58, 'Holding aloof from self--with unstained mind');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 59, 'Should slay all yonder host, being bid to slay,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 60, 'He doth not slay; he is not bound thereby!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 61, 'Knowledge, the thing known, and the mind which knows,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 62, 'These make the threefold starting-ground of act.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 63, 'The act, the actor, and the instrument,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 64, 'These make the threefold total of the deed.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 65, 'But knowledge, agent, act, are differenced');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 66, 'By three dividing qualities. Hear now');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 67, 'Which be the qualities dividing them.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 68, 'There is \"true\" Knowledge. Learn thou it is this:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 69, 'To see one changeless Life in all the Lives,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 70, 'And in the Separate, One Inseparable.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 71, 'There is imperfect Knowledge: that which sees');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 72, 'The separate existences apart,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 73, 'And, being separated, holds them real.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 74, 'There is false Knowledge: that which blindly clings');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 75, 'To one as if \'twere all, seeking no Cause,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 76, 'Deprived of light, narrow, and dull, and \"dark.\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 77, 'There is \"right\" Action: that which being enjoined--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 78, 'Is wrought without attachment, passionlessly,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 79, 'For duty, not for love, nor hate, nor gain.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 80, 'There is \"vain\" Action: that which men pursue');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 81, 'Aching to satisfy desires, impelled');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 82, 'By sense of self, with all-absorbing stress:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 83, 'This is of Rajas--passionate and vain.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 84, 'There is \"dark\" Action: when one doth a thing');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 85, 'Heedless of issues, heedless of the hurt');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 86, 'Or wrong for others, heedless if he harm');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 87, 'His own soul--\'tis of Tamas, black and bad!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 88, 'There is the \"rightful\"doer. He who acts');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 89, 'Free from self-seeking, humble, resolute,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 90, 'Steadfast, in good or evil hap the same,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 91, 'Content to do aright-he \"truly\" acts.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 92, 'There is th\' \"impassioned\" doer. He that works');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 93, 'From impulse, seeking profit, rude and bold');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 94, 'To overcome, unchastened; slave by turns');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 95, 'Of sorrow and of joy: of Rajas he!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 96, 'And there be evil doers; loose of heart,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 97, 'Low-minded, stubborn, fraudulent, remiss,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 98, 'Dull, slow, despondent--children of the \"dark.\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 99, 'Hear, too, of Intellect and Steadfastness');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 100, 'The threefold separation, Conqueror-Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 101, 'How these are set apart by Qualities.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 102, 'Good is the Intellect which comprehends');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 103, 'The coming forth and going back of life,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 104, 'What must be done, and what must not be done,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 105, 'What should be feared, and what should not be feared,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 106, 'What binds and what emancipates the soul:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 107, 'That is of Sattwan, Prince! of \"soothfastness.\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 108, 'Marred is the Intellect which, knowing right');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 109, 'And knowing wrong, and what is well to do');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 110, 'And what must not be done, yet understands');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 111, 'Nought with firm mind, nor as the calm truth is:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 112, 'This is of Rajas, Prince! and \"passionate!\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 113, 'Evil is Intellect which, wrapped in gloom,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 114, 'Looks upon wrong as right, and sees all things');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 115, 'Contrariwise of Truth. O Pritha\'s Son!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 116, 'That is of Tamas, \"dark\" and desperate!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 117, 'Good is the steadfastness whereby a man');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 118, 'Masters his beats of heart, his very breath');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 119, 'Of life, the action of his senses; fixed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 120, 'In never-shaken faith and piety:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 121, 'That is of Sattwan, Prince! \"soothfast\" and fair!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 122, 'Stained is the steadfastness whereby a man');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 123, 'Holds to his duty, purpose, effort, end,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 124, 'For life\'s sake, and the love of goods to gain,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 125, 'Arjuna! \'tis of Rajas, passion-stamped!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 126, 'Sad is the steadfastness wherewith the fool');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 127, 'Cleaves to his sloth, his sorrow, and his fears,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 128, 'His folly and despair. This--Pritha\'s Son!--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 129, 'Is born of Tamas, \"dark\" and miserable!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 130, 'Hear further, Chief of Bharatas! from Me');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 131, 'The threefold kinds of Pleasure which there be.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 132, 'Good Pleasure is the pleasure that endures,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 133, 'Banishing pain for aye; bitter at first');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 134, 'As poison to the soul, but afterward');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 135, 'Sweet as the taste of Amrit. Drink of that!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 136, 'It springeth in the Spirit\'s deep content.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 137, 'And painful Pleasure springeth from the bond');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 138, 'Between the senses and the sense-world. Sweet');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 139, 'As Amrit is its first taste, but its last');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 140, 'Bitter as poison. \'Tis of Rajas, Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 141, 'And foul and \"dark\" the Pleasure is which springs');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 142, 'From sloth and sin and foolishness; at first');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 143, 'And at the last, and all the way of life');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 144, 'The soul bewildering. \'Tis of Tamas, Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 145, 'For nothing lives on earth, nor \'midst the gods');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 146, 'In utmost heaven, but hath its being bound');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 147, 'With these three Qualities, by Nature framed.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 148, 'The work of Brahmans, Kshatriyas, Vaisyas,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 149, 'And Sudras, O thou Slayer of thy Foes!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 150, 'Is fixed by reason of the Qualities');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 151, 'Planted in each:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 152, 'A Brahman\'s virtues, Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 153, 'Born of his nature, are serenity,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 154, 'Self-mastery, religion, purity,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 155, 'Patience, uprightness, learning, and to know');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 156, 'The truth of things which be. A Kshatriya\'s pride,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 157, 'Born of his nature, lives in valour, fire,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 158, 'Constancy, skilfulness, spirit in fight,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 159, 'And open-handedness and noble mien,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 160, 'As of a lord of men. A Vaisya\'s task,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 161, 'Born with his nature, is to till the ground,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 162, 'Tend cattle, venture trade. A Sudra\'s state,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 163, 'Suiting his nature, is to minister.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 164, 'Whoso performeth--diligent, content--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 165, 'The work allotted him, whate\'er it be,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 166, 'Lays hold of perfectness! Hear how a man');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 167, 'Findeth perfection, being so content:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 168, 'He findeth it through worship--wrought by work--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 169, 'Of Him that is the Source of all which lives,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 170, 'Of HIM by Whom the universe was stretched.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 171, 'Better thine own work is, though done with fault,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 172, 'Than doing others\' work, ev\'n excellently.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 173, 'He shall not fall in sin who fronts the task');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 174, 'Set him by Nature\'s hand! Let no man leave');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 175, 'His natural duty, Prince! though it bear blame!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 176, 'For every work hath blame, as every flame');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 177, 'Is wrapped in smoke! Only that man attains');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 178, 'Perfect surcease of work whose work was wrought');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 179, 'With mind unfettered, soul wholly subdued,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 180, 'Desires for ever dead, results renounced.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 181, 'Learn from me, Son of Kunti! also this,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 182, 'How one, attaining perfect peace, attains');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 183, 'BRAHM, the supreme, the highest height of all!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 184, 'Devoted--with a heart grown pure, restrained');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 185, 'In lordly self-control, forgoing wiles');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 186, 'Of song and senses, freed from love and hate,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 187, 'Dwelling \'mid solitudes, in diet spare,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 188, 'With body, speech, and will tamed to obey,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 189, 'Ever to holy meditation vowed,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 190, 'From passions liberate, quit of the Self,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 191, 'Of arrogance, impatience, anger, pride;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 192, 'Freed from surroundings, quiet, lacking nought--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 193, 'Such an one grows to oneness with the BRAHM;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 194, 'Such an one, growing one with BRAHM, serene,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 195, 'Sorrows no more, desires no more; his soul,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 196, 'Equally loving all that lives, loves well');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 197, 'Me, Who have made them, and attains to Me.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 198, 'By this same love and worship doth he know');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 199, 'Me as I am, how high and wonderful,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 200, 'And knowing, straightway enters into Me.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 201, 'And whatsoever deeds he doeth--fixed');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 202, 'In Me, as in his refuge--he hath won');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 203, 'For ever and for ever by My grace');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 204, 'Th\' Eternal Rest! So win thou! In thy thoughts');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 205, 'Do all thou dost for Me! Renounce for Me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 206, 'Sacrifice heart and mind and will to Me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 207, 'Live in the faith of Me! In faith of Me');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 208, 'All dangers thou shalt vanquish, by My grace;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 209, 'But, trusting to thyself and heeding not,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 210, 'Thou can\'st but perish! If this day thou say\'st,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 211, 'Relying on thyself, \"I will not fight!\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 212, 'Vain will the purpose prove! thy qualities');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 213, 'Would spur thee to the war. What thou dost shun,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 214, 'Misled by fair illusions, thou wouldst seek');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 215, 'Against thy will, when the task comes to thee');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 216, 'Waking the promptings in thy nature set.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 217, 'There lives a Master in the hearts of men');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 218, 'Maketh their deeds, by subtle pulling--strings,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 219, 'Dance to what tune HE will. With all thy soul');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 220, 'Trust Him, and take Him for thy succour, Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 221, 'So--only so, Arjuna!--shalt thou gain--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 222, 'By grace of Him--the uttermost repose,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 223, 'The Eternal Place!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 224, 'Thus hath been opened thee');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 225, 'This Truth of Truths, the Mystery more hid');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 226, 'Than any secret mystery. Meditate!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 227, 'And--as thou wilt--then act!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 228, 'Nay! but once more');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 229, 'Take My last word, My utmost meaning have!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 230, 'Precious thou art to Me; right well-beloved!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 231, 'Listen! I tell thee for thy comfort this.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 232, 'Give Me thy heart! adore Me! serve Me! cling');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 233, 'In faith and love and reverence to Me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 234, 'So shalt thou come to Me! I promise true,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 235, 'For thou art sweet to Me!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 236, 'And let go those--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 237, 'Rites and writ duties! Fly to Me alone!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 238, 'Make Me thy single refuge! I will free');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 239, 'Thy soul from all its sins! Be of good cheer!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 240, '[Hide, the holy Krishna saith,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 241, 'This from him that hath no faith,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 242, 'Him that worships not, nor seeks');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 243, 'Wisdom\'s teaching when she speaks:');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 244, 'Hide it from all men who mock;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 245, 'But, wherever, \'mid the flock');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 246, 'Of My lovers, one shall teach');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 247, 'This divinest, wisest, speech--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 248, 'Teaching in the faith to bring');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 249, 'Truth to them, and offering');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 250, 'Of all honour unto Me--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 251, 'Unto Brahma cometh he!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 252, 'Nay, and nowhere shall ye find');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 253, 'Any man of all mankind');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 254, 'Doing dearer deed for Me;');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 255, 'Nor shall any dearer be');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 256, 'In My earth. Yea, furthermore,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 257, 'Whoso reads this converse o\'er,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 258, 'Held by Us upon the plain,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 259, 'Pondering piously and fain,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 260, 'He hath paid Me sacrifice!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 261, '(Krishna speaketh in this wise!)');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 262, 'Yea, and whoso, full of faith,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 263, 'Heareth wisely what it saith,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 264, 'Heareth meekly,--when he dies,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 265, 'Surely shall his spirit rise');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 266, 'To those regions where the Blest,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 267, 'Free of flesh, in joyance rest.]');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 268, 'Hath this been heard by thee, O Indian Prince!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 269, 'With mind intent? hath all the ignorance--');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 270, 'Which bred thy trouble--vanished, My Arjun?');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 271, 'Arjuna.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 272, 'Trouble and ignorance are gone! the Light');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 273, 'Hath come unto me, by Thy favour, Lord!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 274, 'Now am I fixed! my doubt is fled away!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 275, 'According to Thy word, so will I do!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 276, 'Sanjaya.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 277, 'Thus gathered I the gracious speech of Krishna, O my King!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 278, 'Thus have I told, with heart a-thrill, this wise and wondrous thing');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 279, 'By great Vyasa\'s learning writ, how Krishna\'s self made known');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 280, 'The Yoga, being Yoga\'s Lord. So is the high truth shown!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 281, 'And aye, when I remember, O Lord my King, again');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 282, 'Arjuna and the God in talk, and all this holy strain,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 283, 'Great is my gladness: when I muse that splendour, passing speech,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 284, 'Of Hari, visible and plain, there is no tongue to reach');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 285, 'My marvel and my love and bliss. O Archer-Prince! all hail!');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 286, 'O Krishna, Lord of Yoga! surely there shall not fail');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 287, 'Blessing, and victory, and power, for Thy most mighty sake,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 288, 'Where this song comes of Arjun, and how with God he spake.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 289, 'HERE ENDS, WITH CHAPTER XVIII.,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 290, 'Entitled \"Mokshasanyasayog,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 291, 'Or \"The Book of Religion by Deliverance and Renunciation,\"');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (119, 220, 18, 292, 'THE BHAGAVAD-GITA.');
