INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (114, 218, 3, 65, 'May the word of my lips never be changed nor made of no avail.\"\'');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (114, 218, 3, 66, 'Hasten, therefore, and swiftly decree for him the fate which you bestow,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (114, 218, 3, 67, 'That he may go and fight your strong enemy.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (114, 218, 3, 68, 'Gaga went, he took his way and');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (114, 218, 3, 69, 'Humbly before Lahmu and Lahamu, the gods, his fathers,');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (114, 218, 3, 70, 'He made obeisance, and he kissed the ground at their feet.');
INSERT INTO verse (book_id, translation_id, chapter_number, verse_number, body) VALUES (114, 218, 3, 71, 'He humbled himself; then he stood up and spake unto them saying:');
